insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (400, 634, 'https://www.facebook.com/groups/ciovietnam/permalink/1865974466823014', 'Anh Chị quản lý CNTT ở Hà Nội có triển khai quản lý dự án theo phương pháp Agile thì Anh Chị nên tag các Business Analyst, Project Manager của mình tham dự buổi workshop này.Đây là buổi workshop giúp người tham dự thực hành kỹ thuật và hiếm khi được tổ chức ở Hà Nội. Anh Chị đăng ký nhanh vì số lượng chỉ giới hạn có 45 ghế.Cảm ơn Ad trước đã duyệt.#UserStoryThucChien;', to_date('03-07-2018 18:13:00', 'dd-mm-yyyy hh24:mi:ss'), 4, 0, 0);

