insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (476, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/1765554406865021', '"Cộng đồng CIO Vietnam thân gửi anh em nội dung ra mắt chương trình """Hạt giống lãnh đạo CNTT""" sẽ được tổ chức vào ngày mai, cụ thểThời gian: 8h30 thứ 7 ngày 31/3/2018Địa điểm: Én Tea House & RestaurantRobot Tower, 308-308C Dien Bien Phu st., W. 4, D. 3, HCMC, VNThân mời anh em tham dự đăng ký tại http://bit.ly/2DEyyj6#CIOVIETNAM#CIOCOACHING"', to_date('30-03-2018 20:59:00', 'dd-mm-yyyy hh24:mi:ss'), 21, 1, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (473, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/1767989476621514', '"Khi tham gia chương trình coaching """Hạt giống lãnh đạo CNTT""" năm nay bạn sẽ được coaching với 6 chủ đề khác nhau, mỗi chủ đề sẽ liên kết với nhau chặt chẽ và hỗ trợ nhau để giúp bạn làm tốt ở vai trò quản lý, lãnh đạo CNTT trong doanh nghiệp.Nếu bạn nghỉ một buổi, bạn sẽ thiếu nội dung coaching về một chủ đề nào đó trong chương trình. Do đó, trong năm tiêu chí để tuyển chọn 15 hạt giống CNTT năm nay, """Cam kết""" tham gia đầy đủ các buổi coaching là một trong những tiêu chí chúng tôi lựa chọn các hạt giống tham gia chương trình.Nếu bạn thực sự có hoài bão phát triển nghề nghiệp, phát triển bản thân trong lĩnh vực CNTT, thì đây là cơ hội thực sự cần thiết đối với bạn.Chúng tôi nhận đăng ký tham gia chương trình coaching nốt ngày hôm nay, bạn có thể đăng ký tại đây nhé http://bit.ly/2Gr8f3b#CIOVIETNAM#CIOCOACHINGNếu bạn đã định hướng trở thành một nhà lãnh đạo, một nhà quản lý trong lĩnh vực CNTT thì 6 chủ đề trong chương trình coaching """Hạt giống lãnh đạo CNTT""" năm nay thực sự thiết thực với bạn.Mỗi một chủ đề sẽ cho bạn những trải nghiệm, bổ sung những kiến thức mà mình còn thiếu hoặc cần nuôi dưỡng để bạn đi nhanh hơn trên con đường phát triển sự nghiệp của mình.Trải qua 2 năm huấn luyện, BHL cho biết chủ đề """Xây dựng kiến trúc hệ thống CNTT""" là một chủ để tổng quan nhất khi bạn lãnh đạo, quản lý và vận hành một hệ thống CNTT trong doanh nghiệp mà thường bạn ít có cơ hội được tiếp xúc hoặc coaching hiện nay từ chính những kinh nghiệm của 5 thành viên BHL như anh Phạm Văn Trọng- Giám đốc khối CNTT (TGDĐ), một người đã xây dựng và vận hành hệ thống CNTT của TGDĐ từ những ngày đầu tiên tới giờ.Khi bắt đầu xây dựng hệ thống CNTT anh Trọng đã làm thể nào? Sau 15 năm hệ thống đó đã được vận hành và mở rộng ra sao với chiến lược phát triển kinh doanh của TGDĐ như hiện nay?Những cơ hội coaching cuối cùng dành cho bạn tại http://bit.ly/2Gr8f3b#CIOVIETNAM#CIOCOACHING"', to_date('02-04-2018 06:20:00', 'dd-mm-yyyy hh24:mi:ss'), 13, 2, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (474, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/1767038213383307', '"Xin chia sẻ với anh/ chị và các bạn những hình ảnh đẹp tại buổi ra mắt chương trình coaching """Hạt giống lãnh đạo CNTT""" với gần 50 thành viên quan tâm và tìm hiểu về chương trình,Cộng đồng CIO Vietnam cảm ơn anh Peter Phuc Huynh, anh Duc C. Nguyen trong Ban điều hành đã tới tham dự và chia sẻ những tâm huyết của các anh khi xây dựng và phát triển cộng đồng CIO Vietnam từ năm 2010Cảm ơn các anh trong BHL chương trình coaching như anh Đề Đoàn, Cao C. Tran, Cuong Vu, Phan Canh Nhat, Phạm Văn Trọng (Giám đốc khối CNTT- TGDĐ) đã tâm huyết xây dựng chương trình """Hạt giống lãnh đạo CNTT""" chia sẻ những trải nghiệm trong công việc mang lại nhiều giá trị hơn nữa cho doanh nghiệp và các thành viên sẽ tham gia. Chúng tôi hy vọng trong thời gian tới sẽ có nhiều anh/ chị và các bạn có thể cùng tham gia xây dựng cộng đồng hơn nữaCảm ơn các bạn trong team hỗ trợ cộng đồng CIO Vietnam đã dành thời gian tham gia công tác tổ chức, hỗ trợ các hoạt động của CIO Vietnam như Vy Nguyen, Bùi Quang Ngọc Chiêu, Nguyen Hung, Jenny Thai, Vu Kim Anh, Nguyen Son Hai Dang ngày hôm qua và nhiều bạn không tham dự được nhưng vẫn âm thầm ủng hộ, đóng góp vào những hoạt động của cộng đồng.Đam mê và chia sẻ là niềm vui của tất cả những anh/ chị và các bạn khi tham gia tổ chức và hoạt động này là những giá trị mang lại cho cộng đồng như hôm qua các anh/ chị và các bạn cảm nhận.Chúng tôi rất mong tiếp tục nhận được sự ủng hộ và đồng hành từ phía các anh/ chị và các bạn cho các hoạt động sắp tới của cộng đồng CIO VietnamHiện nay chương trình coaching """Hạt giống lãnh đạo CNTT""" vẫn tiếp tục nhận đăng ký tham gia và phỏng vấn trong tháng 4. Link đăng ký anh em có thể xem tại post chia sẻ này.Mến chào và hẹn sớm gặp lại anh/ chị và các bạn <3#CIOVIETNAM#CIOCOACHINGCộng đồng CIO VIetnam và BHL chương trình cảm ơn các anh/ chị và các bạn đã quan tâm và tham dự buổi ra mắt tìm hiểu về chương trình.Qua buổi ra mắt n"', to_date('01-04-2018 09:58:00', 'dd-mm-yyyy hh24:mi:ss'), 38, 7, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (477, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/1764601700293625', '""""Thay đổi bản thân""" đó là giá trị mà anh Khoa Anh Phung chia sẻ với cộng đồng CIO Vietnam sau khóa coaching """Hạt giống lãnh đạo CNTT""" từ năm 2016 do cộng đồng #ITLC tổ chức những ngày đầu tiên.Sau 2 năm, Khoa đã tự tin với vị trí IT Manager của mình và đã mang lại nhiều giá trị hơn nữa cho chính doanh nghiệp mà Khoa đang công tác.Bạn có thể tìm thấy trong câu chuyện của Khoa chia sẻ về những giá trị mà chương trình coaching đã mang lại cho Khoa tại đây https://bit.ly/2pRXpenHoặc có thể tìm hiểu thêm thông tin về chương trình trước khi bạn """dấn thân""" vào một hành chính mới vào lúc 8h30 thứ 7 ngày 31/3/2018 tại Én Tea House & RestaurantRobot Tower, 308-308C Dien Bien Phu st., W. 4, D. 3, HCMC, VNThành công của từng hạt giống sau chương trình là động lực cho cộng đồng CIO Vietnam và Ban huấn luyện chương trình tiếp nối thực hiện mà cộng đồng #ITLC đã thực hiện suốt 2 năm qua."', to_date('30-03-2018 07:33:00', 'dd-mm-yyyy hh24:mi:ss'), 9, 1, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (412, 591, 'https://www.facebook.com/groups/ciovietnam/permalink/1846497405437387', 'Giá trị mà phương pháp Agile có thể mang lạiPhương pháp quản lý dự án nào cuối cùng cũng hướng đến mục tiêu của giá trị khinh doanh (Business Value). Phương pháp chỉ tồn tại khi nó phù hợp với đặc thù của tổ chức và mang lại giá trị. Agile ra đời và đi vào rất nhiều doanh nghiệp lớn trên thế giới vì nó mang lại những giá trị cụ thể:- Đáp ứng nhanh với sự thay đổi: Thế giới trở nên phẳng hơn, môi trường kinh doanh thay đổi nhanh và yêu cầu nghiệp vụ cũng thay nhanh tương ứng. Agile hỗ trợ rất tốt cho việc thay đổi này.- Người làm kinh doanh và đội dự án tương tác tốt hơn: Phương pháp Agile và Scrum Framework đưa ra những hướng dẫn, cơ chế rất rõ ràng để người làm kinh doan và đội kỹ thuật của dự án tương tác face-to-face với nhau thường xuyên. Điều này giúp người làm kinh doanh chuyển tải thông tin tốt hơn với đội dự án, và hạn chế đoán yêu cầu. Đội dự án cũng hiểu tốt hơn các trăn trở của người làm kinh doanh, hiểu hơn về nhu cầu của người làm kinh doanh và tư vấn giải pháp tốt hơn cho người làm kinh doanh. Và giảm thiểu re-work do thay đổi yêu cầu, và đoán yêu cầu tạo ra.- Tốc độ đưa sản phẩm ra thị trường nhanh hơn: Với những yêu cầu mang lại giá trị cao sẽ được ưu tiên phát triển trước và tiếp tục nâng cấp theo thời gian.- Tri thức được ở lại: Với hướng dẫn tổ chức làm việc nhóm chặt chẽ và tài nguyên "tri thức" được chia sẻ giữa các thành viên, giảm thiểu được rủi ro phụ thuộc vào con người- Chất lượng sản phẩm tăng lên: Các cơ chế Agile/Scrum đưa ra rất chặt chẽ để kiểm soát chất lượng sản phẩm. Dường như còn 1 lỗi (bug) đồng nghĩa với thất bại.- Năng suất lao động tăng lên: Ở góc nhìn của chủ doanh nghiệp, nhà quản lý thì đây là điều họ quan tâm nhiều nhất…Những để đạt được những giá trị trên thì cần cả một lộ trình. Mà trước tiên phải hiểu bản chất, nguyên lý của phương pháp. Chỉ cần đội dự án hiểu không giống nhau thì đồng nghĩa phương pháp Agile có thể là một', to_date('20-06-2018 17:12:00', 'dd-mm-yyyy hh24:mi:ss'), 18, 2, 0);

