insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (416, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/1838644819555979', '"TIẾP THỊ 4.0- DỊCH CHUYỂN TỪ TRUYỀN THỐNG SANG CÔNG NGHỆ SỐĐó là chủ đề được tổ chức vào lúc 17h30 thứ 3 ngày 19/6/2018 theo chương trình CIO Debates do cộng đồng CIO Vietnam tổ chức lần thứ 2 trong năm 2018. CIO Debates hoàn toàn khác với các chương trình format trước đây, khi tham gia chương trình CIO Debates thành viên sẽ có nhiều góc nhìn từ những kiến thức, trải nghiệm từ những khách mời tham gia chương trìnhAnh Nguyễn Khoa Hồng Thành, Đồng sáng lập và Operations Director của Isorbar Vietnam, tiền thân là Emerald Consulting (được Dentsu Aegis Network mua lại từ 2015). Anh Hồng Thành cũng là đồng dịch giả của quyển sách """Tiếp thị 4.0""" của Philip Kotler.Anh Cuong Vu, Giám đốc CNTT Công ty Wilmar CLV - thuộc Tập đoàn Wilmar International, một tập đoàn sản xuất và phân phối thực phẩm hàng đầu Châu Á, với hơn 100 công ty thành viên trên toàn thế giớiAnh Vu Thai Ha, Giám đốc công ty INMMA, một chuyên gia tư vấn về quản lý và có kinh nghiệm hơn 20 năm trong ngành CNTT và Viễn thông, với vai trò của người dẫn chương trình và đặt vấn đề.Tiếp thị 4.0- Dịch chuyển từ truyền thống sang công nghệ số""" sẽ mang lại nhiều góc nhìn thú vị về lĩnh vực Marketing đang dịch chuyển từ truyền thống sang công nghệ số và bộ phận IT sẽ bắt kịp xu hướng của lĩnh vực Marketing hay không? IT sẽ làm gì để tiếp nhận và đáp ứng được sự dịch chuyển này? IT sẽ mang lại giá trị gì cho doanh nghiệp khi có sự dịch chuyển từ Marketing sang công nghệ số? Anh/ em bộ phận IT sẽ phải trang bị những kiến thức, kỹ năng nào để cùng đồng hành với bộ phận MKT?Cộng đồng CIO Vietnam trân trọng kính mời Anh/Chị tham dự chương trình CIO Debates tháng 06/2018 với chủ đề “Tiếp thị 4.0 - Dịch chuyển từ truyền thống sang công nghệ số”Link đăng kí: goo.gl/hRiwmY#CIOVIETNAM#CIODEBATES#MARKETING4.0Cộng đồng CIO Vietnam xin phép tag các anh/ chị và các bạn trong Ban điều hành, Ban cố vấn, các admin và các bạn tham gia "', to_date('14-06-2018 20:55:00', 'dd-mm-yyyy hh24:mi:ss'), 22, 0, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (417, 696, 'https://www.facebook.com/groups/ciovietnam/permalink/1837180799702381', '"EVENT CIO DEBATES: TIẾP THỊ 4.0 – DỊCH CHUYỂN TỪ TRUYỀN THỐNG SANG CÔNG NGHỆ SỐ.
IT cần dựa vào kinh doanh để định hướng và xác định mục tiêu. Marketing cần dựa vào IT về mảng công nghệ, tích hợp và ứng dụng chuyên môn. Vậy thì marketing hay IT, cái nào sẽ quan trọng hơn ? Marketing cần IT hơn hay IT sẽ cần Marketing hơn ? Tất cả những câu hỏi về IT và Marketing sẽ được giải đáp tại event “ Tiếp thị 4.0 – Dịch chuyển từ truyền thống sang công nghệ số. “ với góc nhìn đa chiều từ các chuyên gia về công nghệ thông tin và Marketing . Mong rằng chương trình này sẽ là một chia sẻ thú vị và bổ ích dành cho tất cả những đang đã và đang tìm hiểu về chúng.
Thời gian: 17 giờ 30 ngày 19.6.2018
Link tham dự chương trình: https://goo.gl/hRiwmY
#CIODEBATES
#CIOVietnam"', to_date('13-06-2018 20:11:00', 'dd-mm-yyyy hh24:mi:ss'), 4, 0, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (418, 332, 'https://www.facebook.com/groups/ciovietnam/permalink/1835915089828952', '"Cộng đồng CIO Vietnam trân trọng kính mời Anh/Chị tham dự chương trình CIO Debates tháng 06/2018 với chủ đề “Tiếp thị 4.0 - Dịch chuyển từ truyền thống sang công nghệ số”Thời gian: 17h30 thứ 3 ngày 19/06/2018
Địa điểm: Én Event Space, 308- 308C Điện Biên Phủ, Quận 1, HCMLink đăng kí: goo.gl/hRiwmY"', to_date('12-06-2018 19:13:00', 'dd-mm-yyyy hh24:mi:ss'), 13, 3, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (419, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/1833747373379057', '"IT Instrastructure Management- Quản lý cơ sở hạ tầng CNTT, chủ đề đầu tiên trong chương trình """Hạt giống lãnh đạo CNTT""" năm 2018#CIOCOACHING#CIOVIETNAM"', to_date('10-06-2018 13:52:00', 'dd-mm-yyyy hh24:mi:ss'), 34, 10, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (420, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/1834776676609460', '"Marketing và IT có điểm gì chung? Công nghệ, hội nhập, tiến trình, hiện đại hóa, thu thập và phân tích dữ liệu.... nhưng quan trọng hơn hết, marketing và IT muốn chứng minh mang lại giá trị cho doanh nghiệp bằng cách nào? Thông qua việc các hình thức Marketing, giá trị thương hiệu, hãy tăng trải nghiệm khách hàng?Hiện nay Marketing chủ yếu dựa vào những số liệu, những công cụ phân tích hành vi, trải nghiệm, chăm sóc khách hàng để đưa ra những chiến dịch marketing phù hợp với insight của đối tượng khách hàng. Marketing có cần sử dụng công nghệ không? làm thế nào để ứng dụng CNTT vào những hoạt động Marketing trong doanh nghiệp?Và bộ phận IT có hiểu được chức năng, nhiệm vụ của bộ phận MKT, có hiểu được những khó khăn và thách thức của bộ phận MKT khi dịch chuyển từ hình thức Marketing truyền thống sang Digital Marketing? Sự kết hợp giữa hai bộ phận này trợ giúp nhau ở điểm nào? IT sẽ lead marketing hay để Marketing lead IT?Chương trình CIO Debates với chủ đề Tiếp thị 4.0- Dịch chuyển từ truyền thống sang công nghệ số sẽ được chia sẻ từ những khách mời có nhiều kinh nghiệm trong lĩnh vực CNTT và Marketing sẽ mang lại nhiều góc nhìn thú vị dành cho những doanh nghiệp đặt mục tiêu phát triển thị trường, phát triển kinh doanh dựa vào Công nghệ và đang chuyển mình sang công nghệ số trong lĩnh vực Marketing. Chủ đề phù hợp với các Giám đốc điều hành, CIO, CTO, HR, IT manager, Marketing Director, Sales Director, Marketing, IT Manager, System Admin, Digital Marker, những anh/ chị/ bạn quan tâm tới việc dịch chuyển Marketing truyền thông sang công nghệ số.Thời gian: 17h30, thứ 3 ngày 19/6/2018
Link đăng ký tham dự tại goo.gl/hRiwmYNội dung chương trình tiếp tục cập nhật về chủ đề, khách mời...Cộng động CIO Vietnam hy vọng với chủ đề này sẽ rút ngắn những khoảng cách giữa IT & Marketing mang lại hiệu quả cho doanh nghiệp.
#CIODebates
#CIOVIETNAM"', to_date('11-06-2018 13:47:00', 'dd-mm-yyyy hh24:mi:ss'), 14, 4, 0);

