insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (426, 446, 'https://www.facebook.com/groups/ciovietnam/permalink/1816368745116920', 'Trong group mình có anh em nào có công ty oursource IT helpdesk không các bạn?', to_date('23-05-2018 16:03:00', 'dd-mm-yyyy hh24:mi:ss'), 15, 10, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (427, 591, 'https://www.facebook.com/groups/ciovietnam/permalink/1817008908386237', '"Business cần gì ở IT?Một câu hỏi khá thách thức cho các nhà quản lý CNTT đưa ra câu trả lời thoả đáng. Mà ở đó nhà quản lý CNTT cần đặt góc nhìn của mình vào góc nhìn của người làm Business và trả lời một câu hỏi bản chất khác “Các cổ đông muốn gì ở một doanh nghiệp?”. Các cổ đông muốn 3 thứ:
- Lợi nhuận cho khoản đầu tư
- Nguồn lực được sử dụng tối ưu
- Rủi ro kiểm soát ở mức có thể chấp nhận đượcĐó là 3 điều muốn chung nhất ở góc nhìn của cổ đông. Để đạt được 3 điều muốn ấy thì doanh nghiệp triển khai các mục tiêu chiến lược khác nhau theo tình hình thực tế.Quay lại câu hỏi chính “Business cần gì từ IT?”. Bộ phận IT cần align và cân bằng với cái cần của Business và cụ thể là:
- IT chuyển giao các giá trị kinh doanh cho hoạt động kinh doanh
- Nguồn lực của IT cần sử dụng tối ưu và bền vững
- Rủi ro liên quan đến IT được kiểm soát ở mức chấp nhận đượcChung chung là thế, nhưng để hiểu một cách đầy đủ thì COBIT5 có thể giúp bạn. Một framework khá trưởng thành giúp bạn hiểu một cách đầy đủ các mục tiêu kinh doanh, định nghĩa các mục tiêu IT tương ứng và biết khi nào cần triển khai mục tiêu nào và làm sao để triển khai thành công.Một bài toán hóc búa cho các nhà quản lý CNTT.Cao Trần,#caotran; #cobit; #ITGovernance;"', to_date('24-05-2018 08:08:00', 'dd-mm-yyyy hh24:mi:ss'), 45, 21, 0);

