insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (429, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/1814631718623956', 'Chương trình CIO Talks với chủ đề “Khám phá năng lực lãnh đạo bản thân” do Diễn giả- TS. Nguyễn Hồng Phương chia sẻ vào ngày 17/5/2018 đã khép lại với thật nhiều cảm xúc và giá trị đối với 230 members tham dự.Cộng đồng CIO Vietnam xin chia sẻ với anh/ chị và các bạn một clip ghi lại những khoảnh khắc đẹp nhất tại sự kiện lần này như một lời cảm ơn chân thành nhất <3Những khoảnh khắc khác anh/ chị và các bạn có thể xem tại đây goo.gl/fXaBiWCIO Vietnam hẹn sớm gặp lại anh/ chị và các bạn <3', to_date('21-05-2018 18:16:00', 'dd-mm-yyyy hh24:mi:ss'), 17, 0, 0);

