insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (433, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/1810826152337846', 'Dear Anh/ chị và các bạn,Với 230 members tham dự chương trình CIO Talks với chủ đề "Khám phá năng lực lãnh đạo bản thân" do Tiến sĩ Nguyễn Hồng Phương chia sẻ được tổ chức vào lúc 17h30, chiều nay tại 195 Điện Biên Phủ, Phường 15, Quận Bình Thạnh, HCM, cộng đồng CIO xin phép ngưng nhận đăng ký tham dự chương trình tại sự kiện.Để đảm bảo sự kiện diễn ra đúng giờ, anh/ chị và các bạn vui lòng đến sớm checkin lúc 17h30 và chương trình sẽ bắt đầu vào lúc 18hBắt đầu từ sự kiện này, cộng đồng CIO Vietnam sẽ sử dụng ứng dụng Life+ để giúp anh/ chị và các bạn checkin nhanh nhất qua QR Code và cũng sẽ dùng để triển khai trương chình "CIO Vietnam membership card" trong thời gian tới. Khi tham dự sự kiện lần này anh/ chị và các bạn vui lòng checkin tại 4 bàn checkin bằng QR Code đã được hướng dẫn chi tiết gửi tới anh/ chị và các bạn.Cộng đồng CIO Vietnam trân trọng cảm ơn Microsoft và Alpha Books đã tham gia tài trợ chương trình là những cuốn sách phù hợp với chủ đề. Một quầy sách dễ thương sẽ giới thiệu những cuốn sách hay tới anh/ chị và các bạn.Hẹn gặp anh/ chị và các bạn tại sự kiện!', to_date('17-05-2018 15:54:00', 'dd-mm-yyyy hh24:mi:ss'), 17, 1, 0);

