insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (443, 696, 'https://www.facebook.com/groups/ciovietnam/permalink/1802907263129735', '"Tư duy Phát triển (Growth Mindset) - Một nhân tố cơ bản và quan trọng quyết định sự thay đổi (transform) trong mỗi con người và tổ chứcTrước đây chúng ta vẫn thường hay nói cần có Tư duy mở (Open mindset) đối với những điều mới mẻ, thì nhiều năm gần đây người ta thường nhắc đến """Tư duy Phát triển""" nhiều hơn.Người có tư duy Phát triển là có tư duy tích cực, nhìn nhận mọi vấn đề chỉ là một thách thức thông thường hàng ngày và quan trọng để tư duy mở và tiếp nhận những ý tưởng, phản hồi từ bên ngoài cộng với sự sáng tạo nằm ngoài khuôn khổ suy nghĩ thông thường (think out-of-the-box) cũng như bước ra khỏi vùng an toàn (comfort zone) của tư duy thông thường và sau cùng là tìm ra các giải pháp để đối phó với những thách thức và các vấn đề trong cuộc sống và công việc. Người có tư duy Phát triển luôn có suy nghĩ là Làm cách nào để mình làm được (How to make it?) và không có tư duy đi tìm lí do để từ chối hoặc bỏ cuộc trước thách thức khó khăn.Ngược lại, người có tư duy cố hữu (hay tư duy đóng) là tư duy duy trì khuynh hướng lối mòn theo những cách làm thông thường hàng ngày và nằm trong vùng an toàn của mình và sẽ là lực cản cho sự phát triển của cá nhân cũng như tổ chức.Rất nhiều câu chuyện thực tế đã minh chứng cho việc Tư duy Phát triển là tối cần thiết để phát triển cá nhân và tổ chức.- Câu chuyện #1: CEO thứ 3 của Microsoft, Ông Satya Nadella, chú trọng vào việc phát triển Văn hóa công ty với tư duy Phát triển - ông tìm kiếm các nhân viên và lãnh đạo các cấp của Microsoft có tư duy Phát triển làm nhân tố phát triển văn hóa tổ chức và kinh doanh của Microsoft để tiếp tục dẫn đầu trong ngành công nghệ trong thế kỉ 21.- Câu chuyện #2: Về một phụ nữ (doanh nhân trong ngành nước giải khát tên tuổi) đã từng bị tai biến liệt 1/2 người cách đây vài năm. Mặc dù đã qua nhiều bệnh viện lớn và nhiều bác sĩ giỏi ở VN, nhưng các bác sĩ đều thừa nhận là khả năng phục hồi là không t"', to_date('08-05-2018 21:16:00', 'dd-mm-yyyy hh24:mi:ss'), 19, 1, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (444, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/1800645333355928', '"Không gian cho sự kiện ngày 17/5/2018 với chủ đề """Khám phá năng lực lãnh đạo bản thân"""Anh/ chị đã đăng ký tham dự chưa?Link đăng ký https://bit.ly/2KG3pR1"', to_date('06-05-2018 09:57:00', 'dd-mm-yyyy hh24:mi:ss'), 22, 2, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (445, 641, 'https://www.facebook.com/groups/ciovietnam/permalink/1799233280163800', '"Bạn là ai ?
Đang ở đâu ? ?
Và bạn đang làm gì ???
Dành cho những ai đang trên con đường khám phá năng lực của bản thân, băn khoăn, trăn trở để tìm ra đường đi cho chính bản thân mình. Đồng thời muốn tìm ra một phương pháp để có một tư duy tích cực, có đủ kiên nhẫn và can đảm, làm chủ cảm xúc của bản thân, vượt qua mọi khó khăn trong cuộc sống. Cộng đồng CIO Vietnam kính mời các anh chị tham dự chương trình CIO Talks với chủ đề """ Khám phá năng lực lãnh đạo bản thân """.Thời gian: 17h30, thứ 5 ngày 17/5/2018Địa điểm: DreamPlex 2, 195 Điện Biên Phủ, Phường 15, Q. Bình Thạnh, TPHCM.Chương trình chi tiết:- 17h30-18h00: Đón khách- 18h00-18h05: Giới thiệu về CIO Vietnam- 18h05-20h05: Chủ đề “Khám phá năng lực lãnh đạo bản thân”- 20h05-20h30: Networking.Kính mời mọi người tham dự đăng kí tại: https://goo.gl/KSCdbg#CIOTALKS#CIOVietnam"', to_date('04-05-2018 21:05:00', 'dd-mm-yyyy hh24:mi:ss'), 9, 0, 0);

