insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (406, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/1856867391067055', '"Quản lý hạ tầng CNTT có gì... mới không? làm sao quản lý hạ tầng CNTT hiệu quả?Làm sao BOD nhìn nhận vai trò của hạ tầng CNTT trong doanh nghiệp? Anh em IT sẽ có những đề xuất quản trị hạ tầng CNTT như thế nào để mang lại giá trị cho doanh nghiệp, đóng góp vào việc phát triển business.Và business cần gì ở phần hạ tầng CNTT? việc vận hành hạ tầng CNTT hiện nay trong các doanh nghiệp đã thực sự hiệu quả chưa?Cộng động CIO Vietnam xin chia sẻ một góc nhìn từ 4 hạt giống tham gia chương trình """Hạt giống lãnh đạo CNT""" năm nay khi tham gia về chủ đề này.Cộng đồng CIO Vietnam và các hạt giống cũng như các thành viên trong cộng đồng chân thành cảm ơn tâm sức, thời gian và nhiệt huyết của Ban huấn luyện chương trình là các anh Đoàn Đức Đề, Cuong Vu, Cao C. Tran, Phan Canh Nhat, Trọng Phạm VănChúng tôi sẽ tiếp tục chia sẻ những thông tin về chương trình theo từng chủ đề tiếp theo.Cảm ơn 4 hạt giống tham gia chương trình đã ghi nhận chân thực nhất qua chủ đề đầu tiên này.NHẬT KÝ HÀNH TRÌNH LEO NÚIIT INFRASTRUCTURE MANAGEMENT”Đó là chủ đề đầu tiên chúng tôi bước vào chương trình huấn luyện “Hạt giống lãnh đạo CNTT”, bốn con người xa lạ, tuổi đời, kinh nghiệm, công việc, tích cách và từ bốn doanh nghiệp khác nhau nhưng chúng tôi có chung một mục tiêu trở thành những lãnh đạo CNTT thực thụ. Và chủ đề này là thách thức đầu tiên của chúng tôi để bắt đầu hành trình leo núi và cùng đưa nhau lên đỉnh núi cao nhất.Và chúng tôi bắt đầu!Chủ đề chúng tôi gặp đầu tiên là “ IT Infrastructure Management” . Chúng tôi, những người đã làm trong lĩnh vực này hàng chục năm, vậy nên khi nghe đến chủ đề này, trong lòng chợt nở nụ cười đắc ý """Hạ tầng ư, đơn giản vậy” (Tất nhiên chúng tôi đủ thông minh để không nói cho các bác HLV ý nghĩ thật của mình).Hehe, Có vẻ hành trình leo núi dễ hơn nhiều so với những gì chúng tôi tưởng tượng!Nhưng ngay khi nhận được tài liệu bắt đầu hành trình, chúng tôi đã "', to_date('27-06-2018 16:48:00', 'dd-mm-yyyy hh24:mi:ss'), 15, 0, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (407, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/1856541501099644', '"Cộng đồng CIO Vietnam xin chia sẻ với anh/ chị và các bạn những khoảnh khắc ghi lại chương trình CIO Debates với chủ đề """Tiếp thị 4.0- Dịch chuyển từ truyền thống sang công nghệ số""" với gần 90 thành viên tham dự vào ngày 19/6/2018Chủ đề tiếp theo về Membership và Loyalty sẽ được tổ chức vào ngày 26/7/2018. Có bao nhiêu anh/ chị và các bạn quan tâm về chủ đề này?Cộng đồng CIO Vietnam chân thành cảm ơn các anh/ chị đã tham dự chương trình CIO Debates với chủ đề """Tiếp thị 4.0 chuyển đổi từ truyền thống sang công nghệ số""" vào ngày 19/6/2019Chủ đề lần này với sự tham dự của hơn 80 anh/ chị với nhiều góc nhìn thực tế, thú vị và trải nghiệm từ các khách mời là anh Vu Thai Ha, anh Cuong Vu, anh Nguyễn Khoa Hồng Thành giữa hai bộ phân Marketing và IT sẽ kết hợp với nhau như thế nào trong việc chuyển đổi Marketing 4.0 cũng như những ứng dụng Công nghệ 4.0. Cộng đồng CIO Vietnam cảm ơn các anh đã dành thời gian chia sẻ với cộng đồng <3Cộng đồng CIO Vietnam cảm ơn các anh/ chị trong Ban cố vấn, ban điều hành anh Peter Phuc Huynh, Duc C. Nguyen, Nguyen Hong Phuong, Nguyễn Bá Quỳnh, Huan Tran, đã luôn định hướng, đồng hành cùng cộng đồng <3Cảm ơn các bạn admin và các bạn hỗ trợ các hoạt động cộng đồng như Thủy Đặng, Vy Nguyen, Nguyễn Thái Thảo Trang, Vu Kim Anh, Trần Hải, Phan Quang, Nguyen Hung, Bạch Kim Ngân, Nguyen Ngoc Trang, Vân Hoàng đã tham gia tổ chức chủ đề lần này <3Chủ đề tiếp theo về Membership và Loyaty sẽ được cộng đồng CIO Vietnam tổ chức vào ngày 26/7/2018Thông tin về việc tài trợ cho sự kiện ngày 26/7/2018, vui lòng liên hệ Ms Thủy Đặng, email: ciovietnamteam@gmail.com, điện thoại: 0938 298 278Hẹn gặp lại các anh/ chị và các bạn vào tháng 7#CIOVietnam"', to_date('27-06-2018 10:27:00', 'dd-mm-yyyy hh24:mi:ss'), 14, 0, 0);

