insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (331, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/2024555214298271', '"Chủ đề MIS cực kỳ biểu cảm :))Liệu đi học có dễ dàng ? MIS ( Management Information System ) dễ hay khó ?
