insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (496, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/1742664355820693', '"CHƯƠNG TRÌNH HOẠT ĐỘNG NĂM 2018 CỦA CIO VIETNAMCộng đồng CIO Vietam được thành lập từ năm 2010 với mục đích gắn kết các nhà quản lý, lãnh đạo và nguồn nhân lực tiềm năng qua các hoạt động thiết thực như học hỏi, kết nối và chia sẻ những giá trị hữu ích, đa dạng ứng dụng CNTT trong nhiều lĩnh vực khác nhau.Những hoạt động của cộng đồng CIO Vietnam dựa trên những nhu cầu thực tế của các doanh nghiệp tại Việt Nam và khu vực, đồng thời, liên tục cập nhật nhanh nhất những xu hướng phát triển CNTT trên thế giới mang lại giá trị thiết thực và có tính ứng dụng cao cho cộng đồng CNTT.Với những thành viên điều hành cùng ban tư vấn tâm huyết, giàu kinh nghiệm, CIO Vietnam có sứ mệnh xây dựng và phát triển một cộng đồng cho những nhà lãnh đạo, quản lý trong lĩnh vực CNTT và những thành viên mong muốn trở thành những nhà quản lý tương laiTrong năm 2018, CIO Vietnam sẽ có những chương trình hoạt động như1. CIO Trend: Cập nhật xu hướng công nghệ mới hàng năm là điều không thiếu đối trong lĩnh vực CNTT. CIO Trend được tổ chức 2 lần trong năm để cập nhật xu hướng cộng nghệ và ứng dụng trên thế giới.2. CIO Debate: Chia sẻ những góc nhìn, trải nghiệm khác nhau trong lĩnh vực CNTT. Qua chương trình này thành viên sẽ có nhiều góc nhìn khác nhau từ một vấn đề ứng dụng CNTT3. CIO Talk: Chia sẻ những kiến thức, kỹ năng, chuyên môn ứng dụng CNTT để phát triển nghề nghiệp cũng như ứng dụng CNTT cho doanh nghiệp.4. Chương trình coaching """Hạt giống Lãnh đạo CNTT""" với mục tiêu- Tìm kiếm và huấn luyện tối đa 15 thành viên trở thành hạt giống, lãnh đạo và quản lý CNTT- Nâng tầm ứng dụng CNTT trong doanh nghiệp- Tạo nền tảng chia sẻ kiến thức CNTT trong cộng đồng- Phát triển nguồn nhân lực CNTT đủ Tâm và Tầm cho xã hộiChương trình phù hợp với các IT Manager, Head of IT, IT Director muốn trở thành CIO trong tương laiBạn sẽ quan tâm tới chương trình nào trên đây? hãy comment nhé!Bạn muốn hợp tác v"', to_date('08-03-2018 18:10:00', 'dd-mm-yyyy hh24:mi:ss'), 35, 0, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (497, 36, 'https://www.facebook.com/groups/ciovietnam/permalink/1741495619270900', 'I''m inquiring on the interest within CIO Club for a presentation on the European Privacy Directive (GDPR) from CIO members. This will affect all companies dealing with European customers or suppliers.', to_date('07-03-2018 15:33:00', 'dd-mm-yyyy hh24:mi:ss'), 3, 2, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (495, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/1743342679086194', '"Tại Vietnam Blockchain Week trong tuần này với số lượng người tham gia gần 2000 người, trong đó có 50 diễn giả đến từ nhiều nước khác nhau với nhiều chủ đề khác nhau.Cộng đồng CIO Vietnam dự kiến sẽ tổ chức một chủ để về Blockchain được chia sẻ từ một trong 50 diễn giả này. Nhưng hiện nay số lượng thành viên quan tâm chưa được 200 member, hỏi1. Công nghệ Blockchain mới chỉ là xu hướng, chưa áp dụng được, nên đợi?
2. Chưa hiểu, chưa biết bắt đầu từ đâu để tìm hiểu và áp dụng?
3. Business sẽ đi trước, công nghệ sẽ đi sau, khi nào doanh nghiệp yêu cầu tìm hiểu, triển khai mới làm?"', to_date('09-03-2018 07:24:00', 'dd-mm-yyyy hh24:mi:ss'), 26, 1, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (498, 457, 'https://www.facebook.com/groups/ciovietnam/permalink/1741723122581483', 'Dữ liệu vận hành của doanh nghiệpTrong thế giới của CNTT hiện nay chắc hẳn rằng không một chủ doanh nghiệp nào không biết đến dữ liệu hoạt động mà doanh nghiệp mình cần phải có, ít nhất là để ghi nhận lại các hoạt động, các mối làm ăn kinh doanh với đối tác của mình. Dữ liệu vận hành của doanh nghiệp lưu trữ toàn bộ các hoạt động của doanh nghiệp từ các hoạt động mua hàng, xuất nhập tồn kho, bán hàng, tài chính kế toán, sản xuất, các thông tin về các đối tác, khách hàng, nhân viên của doanh nghiệp hoặc ngay cả về các qui định và sự kiện trong kinh doanh của doanh nghiệp.Thu thập dữ liệuViệc thu thập dữ liệu ngày càng dễ dàng hơn với sự phát triển của hệ thống ERP đảm nhiệm hầu hết các vai trong vận hành bên trong nội bộ của doanh nghiệp như tài chính kế toán, sản xuất,...bán hàng, kinh doanh là hoạt động phức tạp với ẩn bên trong là rất nhiều các hoạt động về thương lượng với khác hàng và ngày nay đã được các nền tảng của CRM thu thập dữ liệu một cách triệt để nhất cho doanh nghiệpTrong quá trình vận hành doanh nghiệp, bạn sẽ thấy xuất hiện thêm rất nhiều loại dữ liệu mà khả năng doanh nghiệp lại chưa có các phần mềm để tự ghi nhận các loại dữ liệu này, nhất là các dữ liệu phân tích, thống kê được doanh nghiệp mua từ các tổ chức cung cấp thống kế, nghiên cứu thị trường về nhằm phục vụ phân tích dữ liệu của doanh nghiệp, hoặc các khu vực vận hành của doanh nghiệp mà ERP và CRM chưa kịp hoặc chưa thể vươn cánh tay của mình tới để ghi nhận số liệu.Để tổ chức tốt hơn các dữ liệu đã được thu thập từ các ứng dụng quản trị và các dữ liệu từ các đối tác,... các doanh nghiệp ngày nay sử dụng Nhà kho dữ liệu mà bạn thường được biết tới như là Data Warehouse ở đó sẽ là nơi tập trung mọi loại dữ liệu nhằm phục vụ cho mục đích phân tích số liệu của các nhà quản trị doanh nghiệp.Trong thời đại ngày nay, việc thu thập dữ liệu không còn là bên trong khuôn khổ của doanh nghiệp, các dữ', to_date('07-03-2018 20:06:00', 'dd-mm-yyyy hh24:mi:ss'), 10, 1, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (499, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/1741361899284272', 'Tại Vietnam Blockchain Week cho biết 60% ngân hàng đã thử nghiệm Blockchain, không biết ở Việt Nam mình đã có ngân hàng nào ứng dụng chưa các anh?Và hiện nay Blockchain sẽ phù hợp và đang ứng dụng ở lĩnh vực nào?Để chuẩn bị cho việc ứng dụng Blockchain doanh nghiệp cần phải làm gì?Đối với mỗi cá nhân làm thế nào để tiếp nhận không chỉ về mặt cộng nghệ mà cả kỹ năng, tương tác với nhau và có niềm tin khi tham gia vào blockchain?Anh/ chị có sẵn sàng tìm hiểu, bắt kịp xu hướng này không?', to_date('07-03-2018 10:51:00', 'dd-mm-yyyy hh24:mi:ss'), 22, 9, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (501, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/1735217123232083', '"AI MUA THƯỢNG ĐẾ?
Giá 1 đô laVào thế kỷ 20, tại một thành phố nhỏ ở phía Tây nước Mỹ, một cậu bé khoảng 10 tuổi cầm trong tay đồng 1 đô la đi đến từng cửa hàng ở ven đường và hỏi rằng: “Xin hỏi, ở đây có bán Thượng Đế không ạ?”. Các chủ cửa hàng hoặc nói không có, hoặc cho là cậu bé phá phách bèn đuổi đi ngay.Khi trời sắp tối, cậu bé vẫn cố gắng đi đến hỏi cửa hàng thứ 69: “Xin hỏi, ở đây có bán Thượng Đế không ạ?”.Ông chủ là một bác khoảng 60 tuổi, tóc đã bạc trắng, nét mặt rất ôn hòa, ông mỉm cười hỏi cậu bé: “Cháu bé này, cháu hãy cho ta biết, cháu muốn mua Thượng Đế để làm gì?”.Có người trả lời mình nên cậu bé cảm động rơi nước mắt, cậu nói với ông chủ rằng cha mẹ của cậu qua đời từ sớm, bây giờ chú đang nuôi cậu. Người chú làm việc ở công trường xây dựng, vài ngày trước bị ngã khỏi giàn giáo, đến nay hôn mê chưa tỉnh lại. Bác sĩ nói, chỉ có Thượng Đế mới có thể giúp được ông ấy. Cậu bé nghĩ rằng nhất định Thượng Đế là một thứ gì đó vô cùng kỳ diệu.“Cháu phải mua Thượng Đế về cho chú của cháu ăn thì vết thương sẽ khỏi”.Nghe xong những lời cậu bé nói, mắt ông chủ cũng rơm rớm nước, ông hỏi: “Cháu có bao nhiêu tiền?”
“1 đô la ạ!”“Cháu bé, thật may giá của Thượng Đế cũng vừa vặn 1 đô la”.Ông chủ nhận lấy đồng xu trong tay cậu bé, lấy một chai nước có dòng chữ “Nụ hôn của Thượng Đế” trên kệ xuống và nói: “Cháu bé, cháu cầm lấy đi này, chú của cháu uống xong bình ‘Thượng Đế’ này thì sẽ khỏe lại thôi”.Cậu bé vô cùng sung sướng hạnh phúc ôm chặt lấy chai nước trong lòng, vui mừng chạy đến bệnh viện. Khi vào đến phòng bệnh, cậu bé reo lên: “Chú ơi, cháu đã mua Thượng Đế về rồi, chú mau khỏe lại chú nhé!”.Ngày hôm sau, một nhóm các chuyên gia y học hàng đầu trên thế giới đã bay đến thành phố nhỏ này, đến bệnh viện nơi chú của cậu bé đang nằm và tiến hành hội chẩn. Không bao lâu sau, chú của cậu bé đã được cứu.Khi chú của cậu bé xuất viện, nhìn thấy hóa đơn tiền viện phí th"', to_date('01-03-2018 10:06:00', 'dd-mm-yyyy hh24:mi:ss'), 34, 0, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (502, 457, 'https://www.facebook.com/groups/ciovietnam/permalink/1733301553423640', '"Một chiến lược triển khai giải pháp quản trị chiếm giá trị không nhỏ trong sự thành công triển khai giải pháp quản trị doanh nghiệp và đồng thời chính chiến lược triển khai sẽ quyết định lớn vào phần chi phí cũng như thời gian tham gia của các thành viên dự án.Bạn có thể lựa chọn 1 trong 3 chiến lược triển khai giải pháp quản trị sau:Big Bang - đây là chiến lược triển khai tổng thể cho toàn bộ các bộ phận của daonh nghiệp đồng thờiPhased - bạn có thể chia dự án của bạn ra làm rất nhều giai đoạn để triển khai, và sẽ mở rộng từ từ giải pháp của bạn cho từng bộ phận một trong doanh nghiệp bạnParallel - đây là phương thức triển khai mà bạn sẽ sử dụng đồng thời giải pháp phần mềm hiện tại và song song với đó bạn triển khai giải pháp của mới.
Big BangNhư bản phân tích ở trên, đây là chiến lược triển khai mang trong mình rủi ro rất lớn, lớn nhất trong 3 chiến lược nhưng bù lại thì nó sẽ có được hai điểm rất lợi (1) thời gian triển khai nhanh và (2) chi phí thấp. Hãy chon Big Bang một khi yêu cầu của nhà đầu tư là cần làm nhanh giải pháp, đưa giải pháp vào vận hành trong doanh nghiệp, nhưng bạn cần phải có rất nhiểu chuẩn bị cho việc triển khaiPhương pháp tiếp cận luôn là vấn đề bạn cần phải xem xét, việc lồng ghép và đưa các qui trình mới vào từng phòng ban và cả doanh nghiệp cần phải được suy nghĩ một cách thấu đáo để tránh hoặc giảm thiểu tối đa các phản ứng phụ của người dùngChuẩn bị giải pháp thật kỹ lưỡng cũng là vấn đề bạn cần phải xem xét, vì với Big Bang là Risk rất lớn, vậy để thành công chỉ còn con dường là bạn làm thế nào để nhận diện và giảm thiểu rủi ro đến mức thấp nhất trong đó giải pháp mà bạn cần triển khai đóng vai trò rất quan trọng - các tính năng của giải pháp đã đáp ứng được bao nhiêu phần trăm người dùng yêu cầu, bao nhêu yêu cầu còn lại cần phải được customization, develop, thời gian develop thế nào, ảnh hưởng của các customize và develop vào hệ thống"', to_date('27-02-2018 14:36:00', 'dd-mm-yyyy hh24:mi:ss'), 11, 0, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (500, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/1733422010078261', 'Một chương trình mới bắt đầu cho chuỗi hoạt động cộng đồng CIO Vietnam hứa hẹn mang lại nhiều giá trị cho doanh nghiệp trong năm 2018.', to_date('27-02-2018 13:19:00', 'dd-mm-yyyy hh24:mi:ss'), 76, 22, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (503, 457, 'https://www.facebook.com/groups/ciovietnam/permalink/1728264210594041', 'Câu chuyện quản trị thu mua của doanh nghiệpMới ngồi xuống, chưa kịp nhấp ngụm cafe nào thì hắn đã phan ngay vào câu chuyện mà không một lời khách sáo nào. Như hằn học đã lâu lắm rồi hắn nói: phòng mua hàng thì cũng là thèng tiêu tiền như cái phòng công nghệ IT của tao chứ thứ gì đâu mà yêu cầu đủ thứ, này nọ. Chưa hết, hắn nhận định luôn: mua hàng, chứ đâu phải sale đâu, mình vác tiền đi mùa muh, ông nào tốt thì mình mua, ông nào không tốt thì đứng đó chờ nhé. Mình có quyền mà. Dĩ nhiên, hắn còn rất nhiều lời nhận xét mà đặt vào đó là sự bực bội với phòng thu mua (purchasing).Hắn là một giám đốc IT, và nay đang là thời điểm mà công ty hắn đang triển khai giải pháp ERP, và đang ở giai đoạn đầu của thời kỳ triển khai giải pháp. Làm ngụm cafe đắng đắng như để nuốt bớt đi sự bực bộ, rồi mới nói.Ông nghĩ câu chuyện mua hàng chỉ là gọi mấy ông nhà cung cấp tới rồi chọn, ông có tiền, ông có quyền ah - đừng có lầm như thế nhé. Hãy nhìn lên tầm quản trị một tí đi - nếu đầu vào ông có giá tốt, thì sản phẩm đầu ra của ông mới có giá tốt, và lúc đó các nhân viên kinh doanh của ông mới dễ dàng hơn chiến thắng các đối thủ của ông. Chưa kể là chất lượng sản phẩm đầu vào mà tốt, sản phẩm đầu ra sẽ tốt, và ông sẽ được lòng khách hàng hơn, và nhân viên kinh doanh của ông sẽ có câu: Sản phẩm của công ty tôi rất bền, rất tốt,... và chưa kể tới việc chi phí bảo hành bảo trì sản phẩm cho khách hàng của ông sẽ giảm xuống, tăng lợi nhuận.Lâu nay ông học nào là đàm phán, thương thảo ông cứ nghĩ chỉ có sale mới có ah, mua hàng cũng thế, cũng đàm phán, cũng cân lên hạ xuống về giá, về phương thức thanh toán với nhà cung cấp sao cho dòng tiền của công ty ông tốt nhất. Đó là tui chưa kể tới việc phải tính toán tới việc tồn kho nếu ông mua về một núi, chất đó rồi thì tiền lưu kho, tiền trả cho nhà cung cấp,... và chưa nói tới hư hỏng, phải đàm phán sao đó để có một kế hoạch giao hàng tốt nhất cho', to_date('22-02-2018 06:54:00', 'dd-mm-yyyy hh24:mi:ss'), 21, 18, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (504, 457, 'https://www.facebook.com/groups/ciovietnam/permalink/1720790338008095', '"Doanh nghiệp chuẩn bị gì cho CMCN 4.0Cách mạng công nghệ 4.0 mang lại rất nhiều thay đổi cho từng hoạt động của con người và ngay cả trong các hoạt động quản trị doanh nghiệp, mà ở đó, công nghệ sẽ tham gia vào rất nhiều hoạt động theo chiều sâu, và như các bạn đã biết, nền công nghệ này sẽ thay thế một số các hoạt động của con người bằng robot. Đây là một big change đổi với hoạt động quản trị của các nhà lãnh đạo, nhưng để chuẩn bị cho big change này lại là một câu chuyện mà các nhà quản trị lại lo lắng. Tôi phải chuẩn bị gì cho doanh nghiệp của tôi để chuẩn bi cho làn sóng thay đổi của CMCN 4.0?Câu hỏi rất thiết thực, nhưng để trả lời và bắt tay vào thực hiện một khi đã có câu trả lời là một vấn đề rất nan giải đối với doanh nghiệp. Chúng ta hãy quay lại với các vấn đề của CMCN 4.0 và tìm kiếm các câu trả lời cũng như cần thiết phải chuẩn bị gìDữ liệuChắc chắn bạn và tôi đều biết rằng CMCN 4.0 xây dựng trên nền tảng về dữ liệu - có câu nói đại khái là với nền công nghệ này thì dữ liệu quí hơn dầu mỏ - và với doanh nghiệp, chúng ta chuẩn bị như thế nào để có dữ liệu, và đó là loại dữ liệu gì
Dữ liệu hoạt động nội tại của doanh nghiệp, bao gồm các loại dữ liệu của kế toán, của kho, của mua hàng, của bán hàng, của sản xuất và thường các dữ liệu này được ERP nuôi dưỡng, khai thác và sử dụng nó - và chắc chắn bạn hiểu ngay là chúng ta nên cần một giải pháp ERP để xây dựng và phát triển loại dữ liệu này để khai thác nó.
Dữ liệu hoạt động tương tác với khách hàng - đã từ lâu rồi, tất cả các doanh nghiệp đều dịch chuyển khách hàng là trung tâm của mọi hoạt động của mình, và dữ liệu liên quan tới khách hàng rất quan trọng, trong đó thường bao gồm các dữ liệu về thông tin khách hàng, dữ liệu lịch sử mua hàng của Khách hàng, dữ liệu về chăm sóc khách hàng của doanh nghiệp bạn, và nhất là dữ liệu để tìm kiếm khách hàng của bạn (thường là bộ phận marketing sẽ khai thác các dữ li"', to_date('14-02-2018 17:18:00', 'dd-mm-yyyy hh24:mi:ss'), 12, 4, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (505, 457, 'https://www.facebook.com/groups/ciovietnam/permalink/1724998284253967', '"2 đô la & 9998 đô laĐộng cơ của một con tàu lớn bị hỏng. Những người chủ tàu đã tìm hết chuyên gia này đến chuyên gia khác nhưng không ai biết cách sửa.Rồi họ đưa tới một ông già, người biết sửa chữa tàu thuyền từ khi còn là một đứa bé. Ông ta mang theo một túi đồ nghề và khi đến, ông lập tức bắt tay vào làm việc. Ông xem xét kỹ chiếc thuyền từ đầu đến cuối.
Hai người chủ tàu đứng đó, quan sát người đàn ông, hy vọng ông ta biết phải làm gì.
Sau khi kiểm tra mọi thứ, ông ta lấy túi đồ nghề và rút ra một cái búa nhỏ. Ông cẩn thận gõ nhẹ vào một cái gì đó. Ngay lập tức, động cơ hoạt động trở lại. Ông cất búa đi. Động cơ đã được sửa xong!Một tuần sau, những người chủ tàu nhận được hóa đơn thanh toán giá 10.000 đô-la!
“Cái gì?”, chủ tàu thốt lên, “Ông ta hầu như chẳng phải làm gì cả!”. Họ gửi cho ông một tin nhắn, “Làm ơn cho chúng tôi xem hóa đơn chi tiết.”
Và, họ nhận lại hóa đơn ghi:
- Đập búa: 2 đô-la.
- Tìm ra nơi cần đập: 9.998 đô-la."', to_date('18-02-2018 22:55:00', 'dd-mm-yyyy hh24:mi:ss'), 28, 3, 0);

