insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (478, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/1746722912081504', '"✴ THÔNG BÁO VỀ CHƯƠNG TRÌNH """HẠT GIỐNG LÃNH ĐẠO CNTT 2018"""“HẠT GIỐNG LÃNH ĐẠO CNTT” là chương trình đặc biệt do cộng đồng CIO Vietnam tổ chức dành cho các thành viên trong cộng đồng CIO Vietnam. Một chương trình coaching đặc biệt chỉ dành cho 15 thành viên được huấn huyện trở thành CIO trong tương lai.Dưới đây là một số thông tin ban đầu về chương trình Mục tiêu của chương trình:- Tìm kiếm và huấn luyện tối đa 15 thành viên trở thành hạt giống, lãnh đạo và quản lý CNTT- Nâng tầm ứng dụng CNTT trong doanh nghiệp- Tạo nền tảng chia sẻ kiến thức CNTT trong cộng đồng- Phát triển nguồn nhân lực CNTT đủ Tâm và Tầm cho xã hội Đối tượng tham gia: Các IT Manager, Head of IT, IT Director có mục tiêu nghề nghiệp trở thành CIO. Tiêu chí tuyển sinh- Là thành viên của Group CIO Vietnam, liked page CIO Vietnam- Học viên là người đúng đối tượng,- Học viên thực sự cần lớp này có đủ HOÀI BÃO, ĐAM MÊ, NHIỆT HUYẾT, TỐ CHẤT, CAM KẾT (Thông quan buổi phỏng vấn trực tiếp với Ban huấn luyện)Học phí: Miễn phíLink đăng ký tham dự lễ ra mắt chương trình tại http://bit.ly/2DEyyj6Phí tham dự: 150k/ MemberPhí tham dự tại sự kiện: 200k/ MemberThông tin chuyển khoảnNgân hàng VCB - CN Gia Laisố tài khoản : 0291002241845Chủ tài khoản: Nguyễn Thảo VyNội dung chuyển khoản: CIOCOACHING_Ho&tenTrong trường hợp không thể tham dự, bạn vui lòng hủy GOING tại sự kiện này!Link đăng ký tham gia vào chương trình huấn luyện tại http://bit.ly/2Gr8f3b"', to_date('12-03-2018 13:40:00', 'dd-mm-yyyy hh24:mi:ss'), 37, 12, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (479, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/1764518486968613', '"Khởi động chương trình huấn luyện """Hạt giống lãnh đạo CNTT""" vào những ngày tháng 3, cộng động CIO Vietnam chúng tôi khá gấp rút để chuẩn bị cho sự ra mắt chính thứcVào lúc 8h30 thứ 7 ngày 31/3/2018Địa điểm: Én Tea House & RestaurantRobot Tower, 308-308C Dien Bien Phu st., W. 4, D. 3, HCMC, VNTrong số 76 thành viên trong cộng đồng #CIOVIETNAM có cả những thành viên tại HN đăng ký vào hỏi chúng tôi chương trình có tổ chức ở HN không? Năm 2018 này chúng tôi chưa có chương trình tại HN nhưng về tầm nhìn thì cóNhưng nếu bạn đã quyết tâm thay đổi và bắt đầu một cuộc hành trình leo núi như Anh Đặng Hải Anh- một hạt giống lãnh đạo CNTT đã được tổ chức tại cộng đồng #ITLC năm 2017 đã Nam tiến từ Hà Nội vào SG để tham khóa huấn luyệnNếu bạn muốn nghe câu chuyện của anh ấyHãy nghe chia sẻ của anh ấy để thấy một hành trình ngoạn mục sau khóa huấn luyện https://bit.ly/2Iaody7Nếu bạn muốn tìm hiểu về chương trình, phương pháp huấn luyện, thành viên ban huấn luyệnHãy đăng ký tham gia tại dây vào 8h30 thứ 7 ngày 31/3/2018 http://bit.ly/2DEyyj6#CIOCOACHING"', to_date('29-03-2018 19:58:00', 'dd-mm-yyyy hh24:mi:ss'), 29, 0, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (480, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/1764342680319527', '"Bắt đầu bất cứ một cuộc hành trình nào cũng bắt đầu bằng nhưng câu hỏi như
Tôi là ai?
Tôi muốn gì?
...
Và rất nhiều điều nữa còn đang băn khoăn, trăn trở để tìm kiếm bản thân hay khao khát một điều gì đó mà chưa rõ ràngTôi là ai? là một trong những chủ đề đầu tiên trong chương trình huấn luyện """Hạt giống lãnh đạo CNTT""" do cộng đồng CIO Vietnam tổ chức sẽ ra mắt vào lúc 8h30, thứ 7, ngày 31/3/2018Địa điểm: Robot Tower, 308-308C Dien Bien Phu st., W. 4, D. 3, HCMC, VNLink đăng ký tham dự tại http://bit.ly/2DEyyj6Mời bạn cùng tìm hiểu về chủ đề này qua bài viết từ huấn luyện viên Cuong Vu chia sẻ tại post bên dưới và hãy trao đổi với anh ấy về chủ đề này nhéTôi là ai?Câu hỏi tưởng chừng đơn giản nhưng lại không dễ trả lời một cách thấu đáo. Và chúng tôi đã bắt đầu chương trình Hạt giống lãnh đạo bằng việc khơi gợi và hướng dẫn cho các bạn học viên trả lời câu hỏi này cho chính bản thân mình.Lý do chúng tôi bắt đầu một chương trình Huấn luyện Hạt giống lãnh đạo CNTT bằng việc trả lời một câu hỏi hầu như không liên quan đến bất kỳ nội dung gì của CNTT đến từ một buổi chiều của năm 2016. Khi đó, ban HLV tranh luận rất nhiều khi “vẽ chân dung” của một IT Manager. Qua quá trình đó, chúng tôi tự nhận ra rằng mỗi một HLV vẽ một “chân dung” riêng, bị ảnh hưởng khá nhiều từ kinh nghiệm, suy nghĩ và phong cách của mình. Và vô hình trung, các định hướng, mục tiêu, kế hoạch đều xoay quay và thể hiện rất rõ việc HLV xác định, mình là ai.Cứ hình dung, mỗi cá nhân chúng ta là 1 viên màu và môi trường xung quanh bao gồm gia đình, công việc, bạn bè… là các ly nước. Không ít thì nhiều, chính bạn sẽ là người thay đổi môi trường xung quanh bạn.Qua nội dung chủ đề “Xây dựng thương hiệu cá nhân”, chúng tôi muốn khơi gợi cho các bạn học viên tự trả lời và tự tìm phương pháp để “hiểu” bản thân mình nhiều hơn (điểm mạnh, điểm yếu, kỹ năng, cảm xúc) cũng như gây dựng một hình ảnh ra thế giới bên"', to_date('29-03-2018 16:03:00', 'dd-mm-yyyy hh24:mi:ss'), 8, 2, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (533, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/1657640794323050', '"“Làm thế nào dùng IT kiểm soát quy trình phục vụ khách hàng? Đó là một câu hỏi mà Cộng đồng CIO Việt Nam nhận được trước Talkshow lần thứ 64 này. Ở Thế giới di động điều đó đã được làm như thế nào? Đội ngũ IT và người đứng đầu đội ngũ ấy đã phải thay đổi điều gì để làm được điều đó?Để thay đổi sang “Tư duy dịch vụ” khách hàng thì cần phải làm những gì? Những tiêu chí nào cần đưa ra và đánh giá tiêu chí đó ra làm sao?IT LÀM BUSINESS, CÓ ĐƯỢC KHÔNG?Câu trả lời sẽ được tìm thấy tại Talkshow lần thứ 64 với chủ đề """Vai trò IT trong chiến lược dịch vụ khách hàng của Thế giới di động"""Kính mời anh/ chị/ bạn quan tâm tham gia ^^Với đội ngũ kỹ thuật IT, chuyển từ duy “hỗ trợ truyền thống” sang “tư duy dịch vụ” có khó không? Và làm sao để làm việc này một cách hiệu quả?Có thể đó là câu hỏi đã và đang hình thành trong tư đầu của các nhà quản lý CNTT. Đối với các bộ phận khác thì không phải quá vấn đề, nhưng với những người xuất thân từ IT thì rất thách thức. Giống như một hòn đá tảng mà lăng hoài mà không nhích được.- Anh em rất thụ động, cứ chờ người dùng cuối nêu ra thì mới giải quyết- Anh em nói toàn ngôn ngữ kỹ thuật máy tính, gây lùng bùng cho người dùng- Nhiều khi người dùng cứ cầm máy lên bấm máy gọi cho phòng IT thì bao ký ức “stress” lại hiện về trong tâm trí, và nhiều người đã than thở “mình có phải là khách hàng, hay IT là thượng đế?”- Khi một vấn đề xảy ra thì anh em chỉ chủ động workaround cho xong việc và để các incident tiếp tục lặp lại gây thất vọng cho người dùng cuối.HaizZa…. Nhiều khi các nhà quản lý CNTT phải thốt lên “Bảo hoài mà anh em chả nghe”.Tổ chức của bạn có quy trình tốt, công cụ tốt, nhưng đội ngũ IT của bạn chưa “tư duy dịch vụ” tốt thì những cố gắng của bạn trở nên vô vọng. “Tư duy dịch vụ” là thứ khó đào tạo nhất.Vậy Thế Giới Di Động đã làm gì để giải quyết vấn đề nan giải này? Có thể đây là một câu hỏi hay mà bạn sẽ nhận được câu trả lời từ an"', to_date('08-12-2017 09:28:00', 'dd-mm-yyyy hh24:mi:ss'), 11, 0, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (534, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/1656086331145163', '"Từ trước tới nay, CNTT luôn đứng vai trò hậu phương trong doanh nghiệp. Nhưng tại Thế Giới Di Động, CNTT đóng vai trò xương sống vô cùng quan trọng và là vũ khí tạo lợi thế cạnh tranh và khác biệt trên thị trường bán lẻ.Ở đó, Giám đốc Khối công nghệ thông tin- anh Phạm Trọng Văn đã làm những gì?.
Câu trả lời sẽ được bật mí vào ngày 14/12 tớiKính mời anh/ chị/ bạn đăng ký tham dự để biết điều bí mật đó :)Điều gì khiến 1,000 khách tham dự sự kiện “Giải mã hệ thống CNTT của Thế Giới Di Động” cách đây vài tháng?Khách tham dự sự kiện trên rất đa dạng, chủ doanh nghiệp có, đối thủ muốn nuốt sống TGDĐ cũng có, các gám đốc CNTT, các trưởng phòng CNTT. Mỗi đối tượng có sự quan tâm khác nhau kiểu như:• Đối thủ: Điểm mạnh của hệ thống CNTT này là gì? Mình có cơ hội nào khai thác điểm yếu của họ?
• Các chủ doanh nghiệp: Cách TGDĐ làm như thế nào để tạo ra một hệ thống mà giúp họ phát triển nhanh thế? Mình có học được gì từ cách làm của họ?
• Các giám đốc CNTT: Chiến lược xây dựng hệ thống thông tin (MIS) của họ như thế nào? Các yếu tố thuận lợi nào đã giúp cho đội ngũ CNTT của họ tạo ra được kết quả cho hoạt động kinh doanh và đáp ứng cho lãnh đạo?
• Các trưởng phòng CNTT: Cách họ xây dựng cơ sở hạ tầng, các hệ thống ứng dụng như thế nào mà nghe hay thế?Đã có nhiều câu hỏi được trả lời, và cũng có nhiều câu hỏi còn là bí mật. Nhưng từ “dịch vụ”, “thoả mãn” có thể là từ được nhắc tới nhiều nhất. Nhân viên tư vấn bán hàng “thoả mãn” khách hàng của mình. Nhân viên IT thì “thoả mãn” nhân viên bán hàng và các bộ phận nghiệp vụ (khách hàng nội bộ của IT). Nhưng với chiến lược “dịch vụ khách hàng” của TGDĐ thì IT xác định khách hàng thật sự của họ bao gồm khách hàng “bên ngoài” (chính là bạn). Đó là cách IT làm business.Có thể bạn đang thốt lên “IT làm business ư!”. Thật là khó tin. Nhưng đó là sự thật!Hãy Going và hoàn thành tủ tục giữa chỗ nhé, vì số lượng dự kiến chỉ có 70 ghế trong "', to_date('06-12-2017 20:49:00', 'dd-mm-yyyy hh24:mi:ss'), 16, 0, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (535, 696, 'https://www.facebook.com/groups/ciovietnam/permalink/1651986571555139', 'Công nghệ thông tin có giúp được gì cho dịch vụ khách hàng không các bạn? Giúp mình vài VD thực tiển nhé. Cám ơn trước.', to_date('02-12-2017 10:58:00', 'dd-mm-yyyy hh24:mi:ss'), 8, 1, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (540, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/1641770999243363', '"Lộ trình phát triển nghề nghiệp IT, có thể hơi truyền thống so với thời điểm SMAC,
Đặc biệt comment từ anh Chinh Dinh có thêm góc nhìn đa chiều về lộ trình này
Cảm ơn chia sẻ này từ anh <3Khi lựa chọn IT là một sự nghiệp, việc phát triển sự nghiệp đó là điều luôn được khát khao hướng tới với những cấp độ khác nhauCấp độ 1: IT Specialist (dành cho những nhân viên IT mới bước vào nghề)
Các kỹ năng mềm ở cấp độ này cần có: phương pháp lập kế hoạch, thực hiện công việc, làm việc nhóm, kỹ năng giao tiếp và lắng nghe, viết mail, báo cáo và kỹ năng trình bày,Cấp độ 2: IT Helpdesk (Dành cho những nhân viên IT hỗ trợ kỹ thuật)
Vị trí này tập trung vào việc hỗ trợ kỹ thuật, xử lý sự cố do đó đòi hỏi IT Helpdesk có kỹ năng lănggs nghe một cách cẩn thận, đặt câu hỏi chính xác và xác định bản thân của vấn đề từ yêu cầu của người dùng. Ở vị trí này cần trang bị các kiến thức và kỹ năng khác liên quan như: làm việc nhóm, giao tiếp qua điện toại, lắng nghe và đặt câu hỏi xác định vấn đề, làm việc với khcáh hàng khso tính, viết tài liệu hỗ trợ kỹ thuậtCấp độ 3: IT Admin (Dành cho trưởng nhóm IT/ Quản trị hệ thống mạng)
Trách nhiệm của một người quản trị hệ thống bao gồm: Thiết kế, triển khai, quản lý và duy trì cơ sở hạ tầng máy tính, hệ thống mạng LAN, mạng diện rộng WAN của tổ chức doanh nghiệp. IT Admin làm việc với một loạt các thành phần phần cứng, cũng như các chương trình mạng và các chức năng của nó. Trong trường hợp hệ thống có sự cố; họ thực hiện nhiệm vụ chuẩn đoán, xử lý sự cố để xác định và giải quyết những vấn đề khó khăn. Việc quản trị hệ thống mạng thường được thực hiện từ thiết bị đầu cuối đến máy tính đặc biệt truy cập thông tin mạng
Trong một số tổ chức hay doanh nghiệp, người quản trị hệ thống cũng sẽ chịu trách nhiệm về các biện pháp an nình. Họ có thể làm việc với các phần mềm chống Virus và các chương trình mã hóa dữ liệu. Hay lfa quản lý việc hạn chế truy cập m"', to_date('21-11-2017 20:44:00', 'dd-mm-yyyy hh24:mi:ss'), 5, 2, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (541, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/1642361972517599', '"IT Master Plan tiếp cận theo hướng Governance.Hoạt động chính của quản trj (Governance) tập trung vào 3 công việc chính:
* Hướng đi: cung cấp hướng chiến lược dưới sức em của bên ngoài và nhu cầu từ các hoạt động kinh doanh bằng các chính sách và kế hoạch lên quy trình kinh doanh, mô hình kinh doanh mới.
* Đánh giá: Đánh giá các giải pháp đề xuất (Proposal/Business Case) từ chính hoạt động của quy trình kinh doanh nhằm tạo đạt được các mục tiêu quả trị.
* Giám sát: Giám sát hoạt động vận hành, các dự án mới xem có đạt được hiệu suất mà hoạt động quản trị đang kỳ vọng,..Bạn thấy đó, chính những hoạt động này tạo ra những dự án mới, những đề xuất cải tiến quy trình làm việc, chính sách,... chúng là những gạch đầu dòng nằm trong IT Master Plan của các nhà quản lý CNTT.Bạn muốn lắng nghe sâu hơn cách hình thành IT Master Plan theo hướng Governance. Hãy going event, hoàn thành thủ tục thanh toán. Speaker Vũ Mai Tùng sẽ nói cho bạn chi tiết hướng đi."', to_date('22-11-2017 12:46:00', 'dd-mm-yyyy hh24:mi:ss'), 4, 0, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (542, 591, 'https://www.facebook.com/groups/ciovietnam/permalink/1642342119186251', '"Quản lý cam kết của stakeholder trong dự án CNTT hiệu quảDự án CNTT được hình thành để giải quyết một vấn đề nào đó của doanh nghiệp hoặc tạo ra một cơ hội mới thúc đẩy thay đổi tích cực trong tổ chức. Dự án có sự tham gia của stakeholders (tạm dịch là các đối tượng). Có thể là Project Sponsor, các trưởng bộ phận nghiệp vụ, nhà cung cấp, người dùng cuối, thành viên đội dự án,... và nhiều thành viên liên đới khác.Mỗi thành viên trong dự án là một mắt xích trong hệ thống quản lý dự án. Mỗi thành viên tham gia dự án đều có mục đích riêng. Và nhiều khi mục đích riêng cá nhân đó chống lại mục tiêu của dự án. Nhiều khi họ tạo ra các thách thức hoặc thậm chí muốn dự án thất bại để đạt được mục đích của riêng mình.Các nguyên nhân có thể đến từ:
• Xung đột lợi ích: Thường hay xảy ra với các trưởng bộ phận nghiệp vụ và người dùng cuối.
• Xung đột chính trị nội bộ: Mỗi doanh nghiệp là một hệ sinh thái mà ở đó không thể tránh khỏi xung đột giữa các nhóm lợi ích. Thường xảy ra ở các vị trí cấp cao trong tổ chức mà có liên đới đến dự án.
• Ngại thay đổi, hoặc sợ mất an toàn: Đây là điểm thường hay gặp ở tổ chức nặng về quản lý mang tính chất nhà nước hoặc chưa ứng dụng CNTT nhiều. Dự án triển khai ERP là điển hình cho tình huống này.
• Sợ thất bại: Có nhiều dự án CNTT vấp phải thất bại trong quá khứ dẫn đến các Stakehoders mất niềm tin và đôi khi sợ đối diện với thất bại. Tình huống này hay xảy ra với người dùng cuối hoặc các bộ phận nghiệp vụ. Có thể họ bị ám ảnh bởi các lời đồn về sự thất bại ở các dự án CNTT tương tự.
• Có thiện cảm không tốt hoặc không tin vào đội dự án: Thường hay xảy ra đối với các trưởng bộ phận nghiệp vụ. Họ không tin vào năng lực của chính Project Manager.Bạn thấy đó, phần lớn hệ thống quản lý của doanh nghiệp đang chạy trên lõi Công nghệ Thông tin. Dự án triển khai ERP là điển hình thường thấy. Trong mắt các stakehoder thường có 2 xu hướng nhìn nhận: CNTT"', to_date('22-11-2017 12:02:00', 'dd-mm-yyyy hh24:mi:ss'), 7, 0, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (547, 696, 'https://www.facebook.com/groups/ciovietnam/permalink/1636181863135610', '"Hoạch định chiến lược CNTT và IT Master Plan luôn là thách thức với doanh nghiệp và CIO. Mỗi CIO luôn tìm kiếm những phương pháp tiếp cận tốt nhất để hoạch định chiến lược CNTT đúng đắn, hiệu quả và mang lại nhữn giá trị thiết thực cho doanh nghiệp.Cộng động CIO kính mời anh/ chị và các bạn tham dự Talkshow lần thứ 63 về chủ đề “Phương pháp tiếp cận xây dựng IT Master Plan trong nghiệp: IT Govemance hoặc Enterprise Architecture""". Hai phương pháp tiến cận này khác gì nhau? Áp dụng 2 phương pháp này vào việc xây dựng IT Master Plan như thế nào?Vai trò của CNTT và CIO trong doanh nghiệp như thế nào? CIO sẽ gặp thách thức nào khi xây dựng IT Master Plan? Những yếu tố nào ảnh hưởng khi triển khai, vận hành và đánh giá hiệu quả mang lại?Tất cả những câu hỏi trên sẽ được chia sẻ những kinh nghiệm thực tế từ 2 chuyên gia với hơn 25 năm hoạt động trong lĩnh vực CNTT: Một CIO kỳ cựu và một chuyên gia tư vấn hoạch định chiến lược, xây dựng các hệ thống ứng dụng CNTT trong doanh nghiệpAgenda17.30-18.00: Welcome18.00-18.30: Xây dựng IT Master Plan theo hướng IT Governance - Diễn giả: Anh Vũ Mai Tùng - Nguyên CIO của OCB, NCB...18.30-19:00: Xây dựng IT Master Plan theo hướng Enterprise Architecture - Diễn giả: Anh Phí Anh Tuấn - Giám Đốc, Founder PAT Consulting, Phó Chủ Tịch Hội Tin Học TP. HCM19:00-19.30: Thảo LuậnKính mời anh/ chị/ các bạn tham gia!Link đăng ký http://goo.gl/XRfRcdPhí tham dự chương trình: 150.000đ/ ngườiSố lượng có hạn: 40Liên hệ: Mrs. Thuỷ - 0938298278 hoặc Ms. Vy 01697906799"', to_date('15-11-2017 20:43:00', 'dd-mm-yyyy hh24:mi:ss'), 14, 0, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (548, 696, 'https://www.facebook.com/groups/ciovietnam/permalink/1634022310018232', '"Theo anh/ chị một CIO để trở thành một CIO cần trang bị những kiến thức chuyên môn, năng lực quản lý và các kỹ năng mềm nào?Thời điểm này, anh/ chị đang cần cải thiện và nâng cao điều gì?Cộng đồng CIO sẽ giúp được gì cho anh/ chị và anh/ chị sẽ chia sẻ được những trải nghiệm gì cho các thành viên trong cộng đồng để cùng nhau phát triển.Hãy comment những vấn đề anh/ chị đang quan tâm để những hoạt động sắp tới từ CIO thực sự hiệu quả và thiết thực đối với các anh chị nhé.Mong tin từ anh/ chị,
CIO team with LOVE <3"', to_date('13-11-2017 09:36:00', 'dd-mm-yyyy hh24:mi:ss'), 10, 0, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (549, 591, 'https://www.facebook.com/groups/ciovietnam/permalink/1628901983863598', 'IT Project Manager chuyên nghiệp và nghệ thuật thiết kế hệ thống quản lý dự ánỞ vai trò Project Manager, nếu bạn đặt câu hỏi với ban giám đốc công ty rằng “Dự án này có thật sự quan trọng với cty không?”. Phần lớn câu trả lời rằng “Rất quan trọng”. Nhưng bản chất của dự án mang tính chất tạm thời, nhiều khi dự án đã hoàn thành được 90% khối lượng công việc cũng có nguy cơ đối diện với thất bại bất cứ lúc nào nếu hệ thống quản lý của dự án đó không vững chắc.Có thể bạn quen dùng từ “quản lý dự án” mà quên rằng để quản lý được thì cần có “hệ thống quản lý”. Vì dự án mang tính chất tạm thời và được tham gia bởi các thành viên từ các phòng ban khác nhau, kể cả đối tác tư vấn, nhà cung cấp thiết bị. Có thể nói thành viên tham gia đội dự án có thể là một đội ô hợp (tuỳ vào bối cảnh của dự án). Có nhiều thành viên là quản lý trực tiếp (hoặc quản lý cấp cao – chức danh) nhưng chỉ tham gia trong dự án với công việc có tầm ảnh hưởng ít. Điều gì xảy ra nếu thành viên đó sử dụng quyền của “chức danh” to vật vã thay vì quyền của “vai trò” chỉ rất hạn hẹp của anh ấy trong dự án? Có thể khá thách thức đối với các Project Manager trong tình huống này.Ở giai đoạn lên kế hoạch quản lý dự án, các Project Manager chuyên nghiệp luôn tập ưu tiên để xây dựng “hệ thống quản lý dự án” của mình và hướng đến các giá trị như:1) Sự trực quan từ cơ cấu tổ chức: Cơ cấu tổ chức là bộ khung của hệ thống quản lý dự án. Cơ cấu tổ chức giúp cho các bên liên quan hình dung rõ ràng số lượng vai trò cần có theo cách tiếp cận dự án của Project Manager; vai trò nào sẽ báo cáo kết quản công việc cho vai trò nào; cách thức tương tác giữa các vai trò một cách trực quan.2) Giá trị của định nghĩa mô tả công việc cho mỗi vai trò: Mô tả công việc cho từng vai trò (có thể giống với Job Description đi tuyển một chức danh). Nhưng mô tả vai trò giúp phụ thuộc vào cách tiếp cận, phương pháp quản lý dự án mà Project Mana', to_date('07-11-2017 11:13:00', 'dd-mm-yyyy hh24:mi:ss'), 21, 6, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (550, 696, 'https://www.facebook.com/groups/ciovietnam/permalink/1632167970203666', 'Ở vai trò nhà quản lý CNTT, bạn nghĩ yếu tố nào là quan trọng để IT Master Plan được ban giám đốc duyệt?', to_date('11-11-2017 08:46:00', 'dd-mm-yyyy hh24:mi:ss'), 6, 0, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (551, 696, 'https://www.facebook.com/groups/ciovietnam/permalink/1631479543605842', '"Các quản lý CNTT thấy CIO Vietnam nên tổ chức chia sẻ các chủ đề gì?
Để tiếp tục mang giá trị cho các quản lý CNTT trong cộng đồng thông qua việc tổ chức các buổi offline chia sẻ bởi các chuyên gia, các nhà quản lý CNTT thành công trong ngành. Cộng đồng CIO Vietnam có kế hoạch tổ chức 2 sự kiện trong poll bên dưới vào thời điểm cuối năm 2017. CIO Vietnam sẽ xem xét tổ chức các chủ đề chia sẻ theo đề xuất từ các quản lý CNTT trong cộng đồng trong năm 2018.
Các chủ đề bạn đề xuất có thể là:
- Chia sẻ cách ứng dụng best practice thành công từ các tổ chức CNTT trên thế giới,
- Chia sẻ xu thế công nghệ,
- Chia sẻ về những case study thành công từ các doanh nghiệp đầu ngành,
- Hoặc chủ đề mà bạn nghĩ quan trọng cho các quản lý CNTT ở Việt Nam.
Sau khi có được đề xuất từ các quản lý CNTT, CIO Vietnam sẽ lên kế hoạch và mời các chuyên gia, quản lý CNTT thành công chia sẻ với các bạn.
Chúng tôi kỳ vọng bạn thêm các đề xuất vào poll bên dưới, hoặc chọn lựa các chủ đề mà bạn thấy cần thiết, hoặc tương tác (liked, share, comment) cho nhiều quản lý CNTT cùng đóng góp để tạo ra giá trị nhiều hơn cho các thành viên trong cộng đồng.
CIO Vietnam chân thành cảm ơn bạn đã đóng góp."', to_date('10-11-2017 13:57:00', 'dd-mm-yyyy hh24:mi:ss'), 10, 1, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (556, 696, 'https://www.facebook.com/groups/ciovietnam/permalink/1580155758738221', '"Thân mời thành viên cộng đồng CIO tham dự sự kiện """Hội thảo Toàn cảnh CNTT-TT Việt Nam- Vietnam ICT Outlook (VIO) là sự kiện quy mô quốc gia được Hội tin học TP HCM tổ chức hàng năm, được bảo trợ bởi Bộ TT- TT và UBND TP.HCM. Với chất lượng thông tin chuyên sâu và giá trị, hội thảo đưa ra tổng quan về mức độ quan tâm và sẵn sàng của doanh nghiệp trước làn sóng công nghiệp 4.0; Chia sẻ và cập nhật bức tranh toàn cảnh về ứng dụng CNTT trong các doanh nghiệp tại TP.HCM; Những cơ hội và thách thức của doanh nghiệp trong đầu tư CNTT cùng những nội dung tọa đàm khác.Hội thảo Toàn cảnh CNTT-TT Việt Nam – Vietnam ICT Outlook (VIO) 2017 lần thứ 22 với chủ đề “Doanh nghiệp và cách mạng công nghiệp lần thứ 4: Cơ hội - Thách thức”, được diễn ra vào:Thời gian: lúc 8 giờ, thứ Tư ngày 20 tháng 09 năm 2017Địa điểm: Tầng 5, GEM CENTER – 08 Nguyễn Bỉnh Khiêm, Q.1, TP.HCMLink đăng ký tham dự sự kiện https://goo.gl/tnyHCV"', to_date('09-09-2017 08:56:00', 'dd-mm-yyyy hh24:mi:ss'), 27, 0, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
