insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (645, 234, 'https://www.facebook.com/groups/ciovietnam/permalink/1054056618014807', 'Vẫn theo báo cáo khảo sát của Gartner, dưới đây là các rào cản lớn ngăn bước các CIO đạt được các mục tiêu của mình trong năm 2016.Các điểm đáng chú ý:- Rào cản lớn nhất là “Skills - Kỹ năng”: Cái mà CIO nhà ta chủ yếu đào sâu là goác độ kỹ thuật, rất ít chú ý đến kỹ năng quản lý và lãnh đạo.- Rào cản thứ 2 là tiền, doanh nghiệp mà không có ngân sách cho công nghệ thông tin thì CIO tài đến mấy cũng không thể thành công được. Rào cản 1 và 2 là “Tiền - Tài” luôn đi cùng với nhau đúng như các cụ nhà ta hay nói.- Rất đáng chú ý “Culture - Văn hóa” hết sức quan trọng khi làm việc môi trường đa quốc gia, khi ta thấy sự quan trong của các nền văn hóa thì Việt Nam đã tham gia WTO được 8 năm, năm nay thì tham gia Hiệp định Đối tác xuyên Thái Bình Dương (TPP), và tháng 12 này cộng đồng AEC thành lập.- Đáng chú ý hơn cả cái mà các CIO nhà ta quan tâm nhất là “Relationships – Quan hệ”, thì thế giới người ta lại xếp ở hàng cuối cùng, đúng là Việt Nam ta các giá trị đảo lộn hết cả.Have a great start of the week!', to_date('11-12-2015 17:03:00', 'dd-mm-yyyy hh24:mi:ss'), 3, 0, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (646, 234, 'https://www.facebook.com/groups/ciovietnam/permalink/1052135394873596', 'Theo báo cáo của Gartner dưới đây là danh sách 10 công nghệ ưu tiên hàng đầu của các CIO trên thế giới trong năm 2016.Còn Việt Nam ta thì sao? Xin mời các bác Cio Vietnam nhà ta cho đánh giá.Nắm bắt được xu hướng thị trường là mấu chốt của thành công.', to_date('07-12-2015 11:40:00', 'dd-mm-yyyy hh24:mi:ss'), 23, 9, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (647, 234, 'https://www.facebook.com/groups/ciovietnam/permalink/1050762401677562', 'Không liên quan gì đến IT nhưng quá ấn tượng.Hôm qua nghe nói Mr. Putin sẽ đọc thông điệp liên bang lần thứ 12 của ông, hôm nay lại thấy các con số của ông ấy.Có thể nhiều người không thích Mr. Putin. Nhưng các con số ông ấy đạt được thì quá ấn tượng.Lãnh đạo Việt Nam nên học theo Mr. Putin. Thuyết phục dân bằng những con số.Các bác CIO cũng nên thuyết phục CEO bằng các con số chứ không chỉ bằng các giải pháp cao siêu và rất chi là hàn lâm.Have a nice weekend!', to_date('04-12-2015 11:40:00', 'dd-mm-yyyy hh24:mi:ss'), 26, 1, 0);

