insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (766, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/744816198938852', '"Software-as-a-Service, năm 2015 này công ty anh/ chị và các bạn có dự định sử dụng phần mềm theo hướng này chưa?
