insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (664, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/1032743036812832', '"Tiếp theo các Talkshow do cộng đồng CIO tổ chức, Talkshow 51 lần này với chủ đề """ Giải pháp cho kênh bán hàng đa diện của ngân hàng bán lẻ""" do Anh Phan Thanh Sơn, Nguyên Tổng Giám đốc Cisco Việt Nam chia sẻ kinh nghiệmCũng tại Talkshow 51 lần này đánh dấu sự hợp tác, chia sẻ kinh nghiệm trong lĩnh vực CNTT giữa hai cộng đồng CIO và ITLC, mang lại nhiều giá trị thiết thực tới các thành viên trong thời gian tới.Thân mời anh/ chị và các bạn quan tâm tới giải pháp cho kênh bán hàng đa diện của các ngân hàng bán lẻ hiện nay.Thời gian: 17h30 - 19h30, thứ 5 ngày 22 tháng 10 năm 2015Địa điểm: Ân Nam Restaurant & Cafe, 52A Trương Định, Phường 7, Quận 3, HCMPhí tham dự: 100.000 VNĐ/ ngườiHẹn gặp lại anh chị và các bạn tại buổi talkshow thứ 5 tuần này!"', to_date('20-10-2015 14:06:00', 'dd-mm-yyyy hh24:mi:ss'), 16, 4, 0);

