insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (775, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/744886822265123', 'Địa điểm sự kiện Talkshow 41- Vai trò CIO trong kỷ nguyên số đã sẵn sàng đón tiếp thành viên cộng đồng CIO tham dự tại Ân Nam Cafe 52 Trương định, Quận 3, HCM', to_date('04-12-2014 17:28:00', 'dd-mm-yyyy hh24:mi:ss'), 26, 8, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (776, 446, 'https://www.facebook.com/groups/ciovietnam/permalink/744774015609737', '"Hôm qua xem demo phần mềm, mục chọn tình trạng hôn nhân có:
