insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (672, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/1024840824269720', '"Cộng đồng CIO Việt Nam chân thành cảm ơn các anh/ chị và các bạn đã tham gia, ủng hộ chương trình từ thiện lần thứ 15- """ KÊT NỐI NHỮNG VẦNG TRĂNG""" vào ngày 27/9/2015. Chương trình đã mang lại một mùa trung thu cho các em nhỏ tại Suối Dây, Tân Châu, Tây Ninh!Thương chúc anh chị và các bạn gia đình bình an, hạnh phúc!Cám ơn các anh chị em và các nhà bảo trợ. Chúng tôi hứa sẽ không quên tiếp tục tìm sự ủng hộ từ quý anh chị trong các chuyến sắp tới. smile emoticonCám ơn các bạn đã không ngại sung sướng tham gia chương trình Chia Sẻ 15 của CIO Cio Vietnam. Trong chương trình lần này, chúng ta được những trải nghiệm hết sức """đã""" như sau:- Các bạn trẻ đã không ngại ca hát, gào thét không ngơi nghỉ trong suốt 3 giờ trên xe.- Gần 100 tình nguyện viên và tất cả đều đổ mồ hôi vì nóng và vì bận rộn sắp xếp hơn 2000 phần quà (rút kinh nghiệm lần sau nên mang theo áo thêm để thay đổi ...)- Các bạn được truyền năng lượng bởi nhóm hoạt náo sinh viên rất dễ thương và chuyên nghiệp.- Các bạn có thể bắt gặp trực tiếp mấy anh Nguyên Tổng Giám đốc, Phó Chủ tịch, Tổng Giám đốc các công ty công nghệ lớn cùng nhau cột dây chì cho một backdrop.- Có mạnh thường quân, góp hết 1 tháng lương hưu 150,000đ ủng hộ chương trình và có mạnh thường quân góp 10% kinh phí của chương trình dưới tên một người khác.- Các bạn có thể cảm nhận mùi khét lẹt của những chiếc đầu chưa hề biết đến nón.- Các bạn có thể gặp hàng trăm trẻ dưới 10 tuổi đi dự trung thu cách nhà hàng mấy cây số mà không hề có bố mẹ đi kèm.- Các bạn có thể gặp các bé biết bơi trước khi biết đi.- Các bạn có thể bắt gặp hình ảnh của đoàn Phật tử trong áo nâu sòng đến làm công quả trong nhà thờ.- Các bạn có thể bắt gặp hình ảnh tự sướng mọi lúc mọi nơi và mọi tư thế.- Các bạn làm sales thì trải nghiệm một cuộc vượt kế hoạch ngoạn mục: thay vì là 2000 cháu nhưng theo Cha xứ thì có khoảng 6000 cháu tham dự.- Các bạn sẽ có những trả"', to_date('30-09-2015 18:52:00', 'dd-mm-yyyy hh24:mi:ss'), 16, 0, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (674, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/1023168591103610', '"Sáng nay, cộng đồng ITLC offline chủ đề """Digital Transformation là gì?"""Thân mời anh/ chị và các bạn quan tâm cùng tham dự :-)"""Yes, I can share!"""Chủ đề: Digital Transformation là gì?Đối tượng: IT Manager/Leader/CTO/CIO/Business LeaderNội dung: Mức tổng quanĐịa điểm: Passion 126 Điện Biên Phủ, P Đa Kao, Quận 1Thời gian: 9-11hChi phí cafe 50.000/ngườiCũng hơi lâu không thấy anh em nào nào xung phong đăng đàn chia sẻ làm kiểu offline sôi nổi lúc trước có nguy cơ """tuyệt chủng""". Thế là Hải Phạm bèn quên ngại ngùng cầm micro một phen :DLý do chia sẻ đề tài này vì trước giờ cũng hay lăn tăn comment chỗ này chỗ kia trên ITLC, giờ trình bày lại có hệ thống, đơn giản để thư giản sáng t7 ;-)Nội dung nhắm vào câu hỏi căn bản Digital Transformation (DT) là gì? Tại sao doanh nghiệp cần quan tâm tới DT? Các biểu hiện của nó? Đâu là mấu chốt của DT? Vai trò của IT thế nào với DT?... Chắc tầm 45 slide nói trong 45 phút, hỏi đáp 15 phút. Tiếng còn lại cafe cho dzui thôi, hehe"', to_date('26-09-2015 07:31:00', 'dd-mm-yyyy hh24:mi:ss'), 8, 0, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
