insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (676, 258, 'https://www.facebook.com/groups/ciovietnam/permalink/1015996558487480', 'Dear all, các CIO cho hỏi có nhà cung cấp dịch vụ nào cung cấp VPS mà có sẵn SQLServer 2012 và Windows Server 2008 R2 có sẵn bản quyền không, vụ này mình chưa rành lắm..Khách hàng yêu cầu dịch vụ VPS có bản quyền..tích hợp sẵn.. để xài ERP nhà giồng được.', to_date('12-09-2015 11:47:00', 'dd-mm-yyyy hh24:mi:ss'), 5, 14, 0);

