insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (786, 446, 'https://www.facebook.com/groups/ciovietnam/permalink/736473763106429', '"Giả sử bạn là chủ một doanh nghiệp lớn. Bạn đầu tư một phần mềm chính cho doanh nghiệp (1 triệu đô). Sao 3-4 năm thì việc tăng trưởng và quản lý các hoạt động kinh doanh không được như ý.
Lúc đó bạn triệu tập cuộc họp và các sếp kinh doanh, marketing, và IT. Trong cuộc, ngoài trừ IT, tất cả mọi người đều cho rằng, do hệ thống ứng dụng không đáp ứng nhu cầu doanh nghiệp nên ảnh hưởng đến hoạt động tăng trưởng.
Và bạn có các đề xuất sau:
1- Nâng cấp phần mềm: 1 triệu đô (nhà cung cấp nói phần mềm này chắc chắn đáp ứng nhu cầu mới của bạn và do trong 4 năm qua bạn không mua dịch vụ 20% năm nên họ tính phần upgrade bằng giá lúc trước bạn mua.
2- Mua một ứng dụng mới: 1 triệu đô
3- Sử dụng một nhà tư vấn chiến lược và kinh doanh (không phải tư vấn IT) để xác định xem có đúng IT là nguyên nhân không: 1 trăm nghìn (nếu kết luận đúng là do IT thì bạn vẩn phải bỏ ra 1 hoặc 2.Bạn sẽ chọn 1, 2, 3 hay 4."', to_date('18-11-2014 16:31:00', 'dd-mm-yyyy hh24:mi:ss'), 22, 41, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (787, 407, 'https://www.facebook.com/groups/ciovietnam/permalink/739768726110266', 'Mình đang cần 1 chuyên gia tư vấn CNTT (người nước ngoài) có chứng chỉ ITIL, Window Server, và Unix/Linux để tham gia 1 dự án tư vấn CNTT. Có ai biết giới thiệu giùm nhé. Tks.', to_date('25-11-2014 14:30:00', 'dd-mm-yyyy hh24:mi:ss'), 5, 1, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (788, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/738137732940032', '"Những hình ảnh tại sự kiện CIO Vietnam Talkshow 40 tổ chức vào ngày 20/11/2014In CIO Vietnam Talkshow 40 Mr. Jim Fitzsimmons gave an interesting presentation about IT Security in Vietnam. There are some facts that are really bad.
Jim also proposed some framework of actions that will help fix the IT Security in businesses.
In the event, audience and speaker also discussed a lot about the trend in using online services such as email, server, application, etc and the matter of security when use these services.
Thank Jim and all the audience very much. We look forward to seeing you in the upcoming eventSee you next CIO Vietnam Talkshow"', to_date('22-11-2014 07:53:00', 'dd-mm-yyyy hh24:mi:ss'), 21, 0, 0);

