insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (792, 446, 'https://www.facebook.com/groups/ciovietnam/permalink/731769096910229', '"Mõi ngày chắc anh em nào dùng điện thoại, sẽ nhận ít nhất 10 tin nhắn mời mua nhà. Phần lớn các tin này bị xóa, mình thấy tiếc tiền cho các công ty này bỏ ra khoản tiếp thị vô ít.
Không biết ở nước ngoài có vụ này không em Trần Thị Minh Huệ ?
Nếu để tiền gởi tin họ có mua được cái BI + CRM để làm tốt chuyện này không em Truong Dai?
Các bác IT nghĩ thế nào?"', to_date('09-11-2014 07:54:00', 'dd-mm-yyyy hh24:mi:ss'), 19, 19, 0);

