insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (795, 446, 'https://www.facebook.com/groups/ciovietnam/permalink/733162546770884', '"Chiều nay nói chuyện với một anh giám đốc của một cty phần mềm, anh ấy nhận xét là triển khai phần mềm ứng dụng cho công ty nước ngoài dễ thành công hơn công ty VN. Và cũng theo anh ấy, điều này là do cty nước ngoài các qui trình đã sẳn có và chuẩn hơn cty trong nước.
Các bạn nhận định thấy nhận xét này đúng không? Nếu đúng thì đâu là cách giải quyết vấn đề."', to_date('11-11-2014 20:43:00', 'dd-mm-yyyy hh24:mi:ss'), 20, 29, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (796, 272, 'https://www.facebook.com/groups/ciovietnam/permalink/727027497384389', 'Chào mọi người, mọi người có thể cho em hỏi kinh nghiệm trường hợp này được không ạ :có một ông A đặt hàng một công ty B sản xuất một phần mềm, requirement chỉ kiểu như : Tôi cần xây dựng một phần mềm quản lý bán hàng trên mobile có tính năng X Y Z. Công ty B tư vấn và phân tích thiết kế xây dựng ra phần mềm cho A. (Vậy công ty B có phải là outsource không ?) B nên tính phí như thế nào (Chi phí phân tích thiết kế + chi phí lập trình kiểm thử) ?Câu chuyện xảy ra là khi bàn giao cho A thì sẽ bàn giao chỉ file chạy hay toàn bộ source code vì ban đầu A nói rằng A chỉ sử dụng cho cửa hàng ông ấy nên không thể lấy giá cao (bán cá nhân) ? mà không cung cấp source code bên A sẽ bị phụ thuộc bên B khi có vấn đề hay cần chỉnh sửa.rồi sau đó hóa ra A sử dụng source code đã mua (với giá bán cá nhân 1 người) để mở ra dịch vụ cung cấp phần mềm đó cho rất nhiều đơn vị bán hàng và mở ra thành doanh nghiệp C cung cấp phần mềm đó.(khi công sức nghiên cứu và phát triển bị bán đi với giá rất rẻ sau đó bị sử dụng làm sản phẩm bán đại trà mà không được gì cả)Vậy trong bài toán như thế này bên B nên làm như thế nào để bảo vệ được lợi ích của mình hoặc nên có một kinh nghiệm thỏa thuận với các bên khác như thế nào mong ai đã kinh qua có thể giúp đỡ em (cho các lần sau)Em xin cám ơn', to_date('31-10-2014 12:21:00', 'dd-mm-yyyy hh24:mi:ss'), 13, 38, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (797, 36, 'https://www.facebook.com/groups/ciovietnam/permalink/733202626766876', '"Today France commemorates the 100 year WW1 tragedy. When the French President was inaugurating a memorial for the slain victims over 3 millions in northern France.
A private plane pulling the message """François Hollande Resign""" Appears in the skies. The French Air force helicopter did try to intercept the aircraft as can be seen in the video.http://api.dmcloud.net/…/…/8ec6d486700e4b86badc9bf765755378…"', to_date('11-11-2014 22:31:00', 'dd-mm-yyyy hh24:mi:ss'), 3, 1, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (798, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/733035040116968', '"CIO VIETNAM TALKSHOW 40“ NHỮNG VẤN ĐỀ BẢO MẬT TẠI VIỆT NAM VÀ TĂNG CƯỜNG BẢO MẬT THÔNG TIN NHƯ THẾ NÀO?”Nhằm tăng cường công tác bảo mật và an toàn thông tin trong bối cảnh hiểm họa mất an toàn thông tin ngày càng gia tăng tại Việt Nam, CIO Việt Nam tổ chức Talkshow lần thứ 40 tại Sài Gòn với chủ đề “ Những vấn đề bảo mật tại Việt Nam và tăng cường bảo mật thông tin như thế nào?” do Ông Jim Fitzsimmons, Sáng lập và Giám đốc MF8, đồng chủ tịch Hội đồng Overseas Security Advisiory chia sẻ cùng cộng đồng CIOThân mời anh/ chị và các bạn tới tham dự:Thời gian: 18h00 – 20h00 thứ 5 ngày 20 tháng 11, 2014
Địa điểm: Ân Nam Cafe, 52A Trương Định, Quận 3, HCM
Nội dung chương trình
- 5h30 – 6h00: Đón khách
- 6h00 – 6h45: Thảo luận và trao đổi về “Những vấn đề bảo mật tại Việt Nam và tăng cường bảo mật thông tin như thế nào?”
- 6h45 – 7h00: Hỏi đáp
- 7h00 -7h30: Giao lưu mởPhí tham dự: 100.000 VNĐ"', to_date('11-11-2014 14:10:00', 'dd-mm-yyyy hh24:mi:ss'), 18, 0, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (799, 446, 'https://www.facebook.com/groups/ciovietnam/permalink/731215443632261', 'Theo một anh CEO của một doanh nghiệp lớn thì "việc ứng dụng CNTT của doanh nghiệp Viêt Nam chưa cao" và nguyên nhân phần lớn do các doanh nghiệp của chúng ta còn trẻ, hầu hết là được dẩn dắt bởi các người chủ thế hệ F1, những anh chủ này đã thành công không cần CNTT.Bạn nghĩ sao về ý kiến này?', to_date('08-11-2014 10:25:00', 'dd-mm-yyyy hh24:mi:ss'), 40, 25, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (800, 374, 'https://www.facebook.com/groups/ciovietnam/permalink/721549491265523', 'ĐỪNG BỎ LỠ cơ hội giao lưu và trao đổi với các chuyên gia an toàn và bảo mật thông tin, đặc biệt cùng đối thoại với các đồng nghiệp là các nhà quản trị CNTT của các ngân hàng và doanh nghiệp lớn tại khu vực miền Nam vào ngày 6/11/2014.', to_date('23-10-2014 18:21:00', 'dd-mm-yyyy hh24:mi:ss'), 36, 6, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (579, 374, 'https://www.facebook.com/groups/ciovietnam/permalink/1437173509703114', '"TUYỂN DỤNG CHUYÊN VIÊN CÔNG NGHỆ THÔNG TIN
Tổng Công ty Cổ phần Bảo Minh có nhu cầu tuyển dụng nhân sự với những thông tin như sau:
- Vị trí tuyển dụng: Chuyên viên hệ thống thông tin ứng dụng
- Số lượng tuyển dụng: 01 (một)
- Hình thức làm việc: Toàn thời gian giờ hành chính
- Địa điểm làm việc: 26 Tôn Thất Đạm, Phường Nguyễn Thái Bình, Quận 1, TP. Hồ Chí Minh
- Quyền lợi: Theo quy định của Bảo Minh và của Nhà nước
Mức lương: Theo thỏa thuận
Yêu cầu giới tính: Nam hoặc Nữ
Yêu cầu độ tuổi: Từ 25 - 35 tuổi
Yêu cầu bằng cấp:
+Tốt nghiệp Đại học, chuyên ngành hệ thống thông tin
+ Có kiến thức cơ bản và/ hoặc kinh nghiệm tối thiểu 02 năm về CSDL, SQL Server, DB2 hoặc Oracle
+ Kỹ năng Anh văn đạt tối thiểu 550 pts TOEIC hoặc tương đươngThành thạo Microsoft Office, máy tính và các thiết bị văn phòng
Ưu tiên:
+ Hiểu biết về các nghiệp vụ bảo hiểm phi nhân thọ
+ Hiểu biết/ kinh nghiệm làm việc trên các hệ thống thông tin Pollsy/Asia (PSEA), SAP FI-CO
Hồ sơ bao gồm:
- Đơn xin việc viết tay.
- Giấy khám sức khỏe gần nhất.
- Sơ yếu lý lịch có dán ảnh (4x6), đóng dấu giáp lai và có xác nhận của chính quyền địa phương.
- Bản photo CMND hoặc Hộ chiếu.
- Bản photo có y sao bản chính: Bằng tốt nghiệp Công nghệ Thông tin (hoặc cao hơn)
- Bản photo các văn bằng, chứng chỉ có liên quan khác (nếu có).
Hình thức và nơi nộp hồ sơ: -Nộp trực tiếp tại:
TỔNG CÔNG TY CỔ PHẦN BẢO MINH
BAN TỔ CHỨC NHÂN SỰ (Lầu 2)
Số 26 Tôn Thất Đạm, Phường Nguyễn Thái Bình, Quận 1, TP. Hồ Chí Minh (Không hoàn lại hồ sơ)
Hạn nộp hồ sơ: 30/04/2017
Website: http://www.baominh.com.vn/…/chuyenmuc-976-thang-4-tintuc-44…
Hotline 24/7: 1800 - 58 88 12
Hoặc email cho mình: lathuylinh@gmail.com"', to_date('14-04-2017 11:23:00', 'dd-mm-yyyy hh24:mi:ss'), 7, 4, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (581, 265, 'https://www.facebook.com/groups/ciovietnam/permalink/1436983149722150', 'Cloud, Colocation or Dedicated Hosting – Which one should you choose?The modern day #IT infrastructure thrives on the framework of #colocation, #dedicatedhosting or #cloudcomputing. Read more to choose the right #technology your #business.https://www.esds.co.in/blog/cloud-colocation-or-dedicated', to_date('14-04-2017 08:40:00', 'dd-mm-yyyy hh24:mi:ss'), 2, 0, 0);

