insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (583, 446, 'https://www.facebook.com/groups/ciovietnam/permalink/1373119382775194', '"Cloud giờ nổi tiếng trong giới IT, mà mình thấy nó vẩn trừu tượng và khó hiểu, đặt biệt với các sếp lãnh đạo doanh nghiệp. Dịch sang tiếng Việt là điện toán đám mây cũng khó hiểu luôn.
