insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (687, 457, 'https://www.facebook.com/groups/ciovietnam/permalink/992282327525570', 'Nếu kể 5 thử thách lớn khi đưa giải pháp quản trị vào vận hành cho doanh nghiệp của mình - thì đó là các thách thức gì vậy các anh CIO?', to_date('23-07-2015 16:20:00', 'dd-mm-yyyy hh24:mi:ss'), 0, 0, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (688, 446, 'https://www.facebook.com/groups/ciovietnam/permalink/982630025157467', '"Hôm nay nói chuyện với một chuyên gia kinh tế, thầy của các hạt giống lãnh đạo. Anh ấy nói là: Doanh nghiệp phải thiết kế chiến lược kinh doanh dự trên chiến lược về công nghệ thông tin, chứ không phải ngược lại. Có như vậy thì doanh nghiệp mới có thể phát triển nhanh trong thời đại kỷ thuật số.
Điều nầy hết sức trái ngược với các bí kiếp của 5 ngũ đại môn phái trên giang hồ. Thật là bối rối. Mong được các bạn đồng đạo trong giới IT chỉ giáo thêm."', to_date('01-07-2015 21:09:00', 'dd-mm-yyyy hh24:mi:ss'), 10, 10, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (689, 36, 'https://www.facebook.com/groups/ciovietnam/permalink/989510541136082', 'TTIP: a Western corporate lobbying paradise (behind closed doors)Who do you think should be writing the terms of the huge trade deal between the EU and the US that’s currently being negotiated? Politicians? European citizens? Or corporate lobbyists, paid hundreds of thousands of Euros a year to push a big business agenda in Brussels?We’ve teamed up with Corporate Europe Observatory to find out who’s lobbying on TTIP -- and it’s pretty disturbing to read.Here are eight infographics that highlight the most important details we’ve uncovered.Upset? Sign the self-organised European Citizens'' Initiative here: http://bit.ly/1w7tdLp', to_date('17-07-2015 10:28:00', 'dd-mm-yyyy hh24:mi:ss'), 0, 0, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
