insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (597, 446, 'https://www.facebook.com/groups/ciovietnam/permalink/1270451973041936', '"Cẩm nang hướng dẫn cho các Giám đốc CNTT về cloud computing (The CIO''s Guide to Cloud Computing)Một cẩm nang hướng dẫn tóm tắt về cloud computing hữu ích cho các nhà quản lý về Công nghệ thông tin (CNTT) (IT) trong các doanh nghiệp bao gồm:
(This is an interesting guide for CIOs on the cloud computing including:)1) Tại sao là cloud? (Why cloud?)
2) Thị trường cloud trên thế giới thế nào? (Cloud market)
3) Mô hình hybrid cloud (Hybrid cloud vs. single panel of glass)
4) Kế hoạch chuyển đổi lên cloud của doanh nghiệp? (Moving to cloud)
5) Vai trò lãnh đạo và kĩ năng về cloud là cần thiết (Needs of IT leadership and cloud skilss)
6) Vấn đề bảo mật thông tin trên cloud? (Security on cloud)Xu hướng gia tăng ứng dụng trên cloud sẽ gia tăng trong 2 năm tới (43%) so với năm nay (30%) như một nghiên cứu về cloud của công ty IDC (Cloud View 2016).Các bạn có thể tải về báo cáo chi tiết trên tạp chí Havard Business Review (HBR) dưới đây (A research report can be downloaded from the below link)
https://hbr.org/…/…/oracle/19790_HBR_Report_Oracle_Cloud.pdf"', to_date('03-11-2016 06:41:00', 'dd-mm-yyyy hh24:mi:ss'), 16, 1, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (598, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/1269535106466956', '"Cảm ơn các anh/ chị và các bạn đã tham dự buổi Talkshow 56 về chủ đề """Ứng dụng điện toán đám mây trong quản lý doanh nghiệp""".Hẹn gặp sớm gặp lại anh/ chị và các bạn với chủ đề Cloud Security trong tháng 11 này tại Sài Gòn :-)"', to_date('02-11-2016 08:21:00', 'dd-mm-yyyy hh24:mi:ss'), 21, 0, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (599, 446, 'https://www.facebook.com/groups/ciovietnam/permalink/1265480363539097', '"Cám ơn các anh chị và các bạn đã tham gia thảo luận trong buổi tọa đàm 56, với chủ đề về cloud và các ứng dụng trên cloud.
Qua buổi tọa đàm chúng tôi đúc kết được một số quan tâm và các trở ngại cho việc tận hưởng các lợi ích của cloud, có thể được kể như sau.
1. An toàn dữ liệu (Security)
2. Đường truyền (Connection)
3. Chi phí bản quyền phần mềm.Để có cái nhìn đa chiều, dự kiến ngày 17/11 CIO sẽ tiếp tục có buổi tọa đàm xoay quanh các vấn đề vừa nêu với các diễn giả có kinh nghiệm trong các lĩnh vực nói trên.Mong được đón tiếp các bạnBTC Talkshow 57th."', to_date('28-10-2016 22:15:00', 'dd-mm-yyyy hh24:mi:ss'), 6, 0, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (600, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/1264537766966690', 'Hình ảnh trực tiếp tại buổi Talkshow 56 với chủ đề "Doanh nghiệp Việt Nam và các ứng dụng điện toán đám mây" *_^', to_date('27-10-2016 18:46:00', 'dd-mm-yyyy hh24:mi:ss'), 53, 1, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (601, 446, 'https://www.facebook.com/groups/ciovietnam/permalink/1251290964958037', 'Điện toán đám mây được nhắc nhiều trong thời gian gần đây, tuy nhiên đa phần vẫn là lý thuyết. Cio Vietnam dự định sẻ có buổi chia sẽ của "người trong cuộc" về ứng dụng điện toán đám mây (cloud) tại Việt Nam, vào 27/10 tới. Anh chị em nào quan tâm xin nhớ đừng hẹn hò chiều tối 27 nhé. Lịch chỉ tiết sẻ gởi sớm đến anh chị.ThânBTC TS 56.', to_date('13-10-2016 08:31:00', 'dd-mm-yyyy hh24:mi:ss'), 51, 14, 0);

