insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (696, 603, 'https://www.facebook.com/groups/ciovietnam/permalink/980113008742502', 'Mình đang muốn làm 1 cổng thanh toán trực tuyến thu phí sản phẩm nhỏ như Fshare, tailieu.vn giá trị mỗi lần giao dịch <100.000VNĐ. MỌi người có biết ngân hàng nào hay đơn vị nào cung cấp dịch vụ này có hoặc không có API giao dịch đều được. Cho mình lời khuyên nhé!', to_date('26-06-2015 14:40:00', 'dd-mm-yyyy hh24:mi:ss'), 1, 2, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (697, 374, 'https://www.facebook.com/groups/ciovietnam/permalink/972671282820008', '"Mùa hè là mùa các bạn học sinh được nghỉ ngơi và tham gia các chuyến du lịch cùng gia đình và bạn bè để giải tỏa những tháng ngày căng thẳng với những kỳ thi vừa qua.
Nhưng đâu đó, mùa hè của những gia đình các bạn nhỏ vùng sâu là mùa với những khó khăn trăm bề
- Giếng cạn, không có nước, không có điện.
- Mùa Điều đã hết, đất thì không còn…
- Việc làm không có…thất nghiệp…
Với mong muốn góp một phần nhỏ công sức của mình để mang lại cuộc sống đỡ khó nhọc cho các gia đình bạn nhỏ ở đây và khích lệ tinh thần học tập của các em. CIO VN mạn phép kêu gọi tổ chức chương trình TỪ TRÁI TIM ĐẾN TRÁI TIM 14 """Mùa Hè Của Trẻ Em Vùng Sâu""".Mọi đóng góp vui lòng gửi về :Lâm Thanh HoàngSTK: 007 1003 166 326 - Ngân Hàng Vietcom bank, Chi nhánh HCM_Bến Chương Dương"', to_date('11-06-2015 16:18:00', 'dd-mm-yyyy hh24:mi:ss'), 27, 2, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (698, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/976272779126525', '"Ứng dụng CNTT trong lĩnh vực xây dựng chưa từng được chia sẻ trong cộng đồng CIO Việt Nam. Trong khi đó, với sự phát triển của CNTT ngày càng nhiều công nghệ mới đã được tạo ra và ứng dụng trong lĩnh vực này này. Những công nghệ mới này đã làm tăng năng suất lao động, cũng như tăng tính hiệu quả công việc, giảm lãng phí trong xây dựng. Một trong những công nghệ mới đó là Building Information Modeling, được viết tắt là BIM, tạm dịch Mô hình thông tin công trìnhBIM được xem như là một công cụ hiệu quả trong việc quản lý và chia sẻ thông tin giữa chủ đầu tư, kiến trúc sư, kỹ sư tư vấn, nhà cung ứng, nhà thầu xây lắp. Sử dụng BIM để quản lý và chia sẻ thông tin về công trình giúp tăng cường cộng tác, phối hợp giữa các thành viên của dự án, đó đó sẽ giảm lãng phí, tăng chất lượng, tính hiệu quả của công việc. BIM là một quá trình trao đổi và chia sẻ thông tin của công trình dưới dạng số hóa. Các nhà tư vấn thiết kế cũng như các nhà thầu xây dựng có thể sử dụng phần mềm BIM để tạo nên một mô hình của công trình trên máy tính mà mô hình này giống hệt như công trình thực tế ở ngoài công trường. Mô hình không gian ba chiều này được liên kết với cơ sở dữ liệu thông tin dự án. BIM thể hiện tất cả cá mối liên hệ về mặt không gian, thông tin hình học, kích thước, số lượng và cả cấu tạo vật liệu của các cấu kiện, bộ phận của công trình. Nó có thể được sử dụng để thể hiện toàn bộ vòng đời xây dựng từ khâu thiết kế, thi công, cho đến khâu vận hành sử dụng.Cộng đồng CIO trân trọng kính mời anh/ chị và các bạn tham dự buổi Talkshow 50 về chủ đề “Phương pháp tiến cận và áp dụng mô hình thông tin công trình” dành cho lĩnh vực xây dựng.
Thời gian: 13h00 đến 19h00 thứ bảy, ngày 20 tháng 6 năm 2015
Địa điểm: Jagar Beer Club, 262 Huỳnh Văn Bánh, Phường 11, Quận Phú Nhuận
Nội dung chương trình
13h00 – 13h30: Đón khách
13h30- 1h45: Một số sản phẩm Autodesk, diễn đàn chuyên sâu của Audodesk và "', to_date('19-06-2015 17:04:00', 'dd-mm-yyyy hh24:mi:ss'), 10, 1, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (699, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/976666075753862', 'Không khí tại buổi Talkshow 50 về BIM với gần 70 thành viên tham dự :-)', to_date('20-06-2015 13:54:00', 'dd-mm-yyyy hh24:mi:ss'), 30, 5, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (700, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/975329072554229', '"Dear Anh/ Chị và các bạnVới mong muốn góp một phần nhỏ bé mang lại cuộc sống đỡ khó khăn cho các gia đình các em nhỏ ở Làng Bùi Tơ - Bình Phước và mang lại một mùa hè ý nghĩa cho các em. Cộng đồng CIO Việt Nam mạn phép kêu gọi chung tay thực hiện chương trình” TỪ TRÁI TIM ĐẾN TRÁI TIM 14” – MÙA HÈ CỦA TRẺ EM VÙNG SÂU bằng cách với 2 gói dịch vụ CNTT1. 10 gói dịch vụ EXA Backup- Sao lưu trữ liệu trực tuyến dung lượng 50GB, thời hạn sử dụng 12 tháng tại http://backup.exa.vn trị giá 4,8 triệu. Thông qua chương trình này chỉ bán 2,4 triệu/ gói2. 03 gói dịch vụ khởi tạo tại www.web4s.vn trị giá 1.428.000 bao gồm. 1 website + 1 tên miền quốc tế + 1 hosting. Chỉ 4 bước đơn giản, người dùng có ngay 1 website thương mại, website bán hàng cho cá nhân, doanh nghiệp trên nền tảng điện toán đám mây với khả năng mở rộng linh hoạt, giao diện website hiển thị trên mọi thiết bị.Toàn bộ số tiền bán các gói dịch vụ trên, cộng đồng CIO cam kết dùng để thực hiện chương trình “TỪ TRÁI TIM TỚI TRÁI TIM 14- MÙA HÈ CỦA TRẺ EM VÙNG SÂU”, cụ thể
- Tài trợ chi phí khoan 01 giếng nước cho làng Bùi Tơ- Bình Phước
- 01 gói nhu yếu phẩm cho gia đình các emRất mong nhận được sự ủng gộ, chung tay thực hiện chương trình này vào ngày 27 và 28/6/2015Chân thành cảm ơn anh/ chị và các bạnNếu bạn đánh chữ “Làng Bùi Tơ – Bình Phước” vào Google. Bạn sẽ có rất rất ít thông tin về nơi này. Bởi vì, nó quá sâu, quá nghèo và quá vất vả!Cuộc sống của Làng Bùi Tơ gắn liền với những gốc điều. Vào mùa hè, khi bao đứa trẻ khác được đi du lịch cùng gia đình, nhưng ở đây, gia đình các em gặp nhiều khó khăn:Giếng cạn, không có nước, không có điện
Mùa điều đã hết nước thì không còn
Việc làm không có… thất nghiệp…Với mong muốn góp một phần nhỏ bé để mang lại cuộc sống đỡ khó khăn cho các gia đình bạn nhỏ ở đây và mang lại một mùa hè ý nghĩa cho các em. Cộng đồng CIO Việt Nam mạn phép kêu gọi chung tay thực hiện chương trình"', to_date('17-06-2015 13:14:00', 'dd-mm-yyyy hh24:mi:ss'), 11, 4, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (805, 591, 'https://www.facebook.com/groups/ciovietnam/permalink/719977704756035', 'Quản lý điểm yếu kỹ thuật – Góc nhìn của dân ngoại đạoKhi nói đến quản lý điểm yếu kỹ thuật (Vulnerability Management) thì trong đầu mình nổi lên những câu hỏi như: Thế nào là điểm yếu kỹ thuật và tầm ảnh hưởng?; Tại sao cần quản lý điểm yếu kỹ thuật?; Quản lý điểm yếu kỹ thuật như thế nào?; Làm sao để triển khai các biện pháp đo lường tính hiệu quả? Áp dụng quản lý điểm yếu kỹ thuật thì IT của doanh nghiệp được gì?1. Thế nào là điểm yếu kỹ thuật và tầm ảnh hưởng? Một câu hỏi nghe có vẻ đơn giản nhưng không biết trả lời thế nào cho đủ,… mình lại nhớ cái vụ VCCorp sập cách đây mấy hôm, rồi cái vụ sập iCoud của Apple hôm tháng 6, hoặc vụ cty Việt Hồng lấy cắp thông tin của khách hàng dùng phần mềm theo dõi trên điện thoại gây xôn xao dư luận, và rất nhiều vấn đề liên quan đến bảo mật dịch vụ CNTT mà báo chí đang tốn rất nhiều giấy mực,… Với những sự cố trên thì các nhà cung cấp dịch vụ ảnh hưởng thế nào? Niềm tin của khách hàng bị giảm sút, khách hàng rời xa dịch vụ hoặc chọn nhà cung cấp dịch vụ khác, các bộ phận khác phàn nàn về dịch vụ của phòng IT, quản lý của cty dịch vụ trên sẽ dương cao cây gậy mà đánh vào KPI cho người thực thi và giám sát dịch vụ CNTT,…!Vấn đề gốc đến từ đâu? Có thể quá trình backup-recovery có nhiều vấn đề, firewall không được cập nhật bản vá liên tục, quá trình cập nhật phần mềm anti-virus không liên tục, người dùng cuối cài phần mềm miễn phí không rõ nguồi gốc và chưa được xác thực bởi sysadmin, thiết bị phần cứng không được giám sát và bảo trì định kỳ,… có đến hàng 1001 lý do gây nhiều điểm yếu kỹ thuật trong CNTT.Vậy chúng ta có thể thấy điểm yếu kỹ thuật là những ', to_date('20-10-2014 12:10:00', 'dd-mm-yyyy hh24:mi:ss'), 16, 3, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (806, 374, 'https://www.facebook.com/groups/ciovietnam/permalink/716220015131804', '"Dialogue 7 - Sai Gon - Quản lý các điểm yếu kỹ thuật trong An Ninh Thông Tin
17/10/2014, từ 11h30 đến 14h tại cafe Ân Nam, 52 Trương Định, Quận 3.
