insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (807, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/713591605394645', '[ĂN TRƯA CHIA SẺ KINH NGHIỆM VỀ BẢO MẬT]Đó là buổi Dialogue 6 với chủ đề về “CRYME CRIME & COMPUTER FORENSICS INVESTIGATION METHODS” – Tội phạm công nghệ cao và các biện pháp phòng chống được cộng đồng CIO tổ chức vào ngày mai (10/10/2014) vào lúc 11h30 đến 14h tại An Nam Café, 52 Trương Định, Quận 3, HCMHiện nay chúng tôi xác nhận có gần 30 thành viên quan tâm và tham dự buổi Dialogue 6 về chủ để này do Ông Karthik Palaniyappan, Giám đốc mảng dịch vụ tư vấn các vấn đề pháp lý liên quan đến máy tính của E&Y Vietnam chia sẻ.Chỉ còn 5 slots duy nhất để đăng ký tham dự. Anh/ chị có thể đăng ký trực tiếp tại post này hoặc inbox cho Red nhé.Một số hình ảnh tại An Nam Café chuẩn bị đón tiếp các thành viên CIO tham dự vào ngày mai.Hẹn gặp anh/ chị và các bạn!', to_date('09-10-2014 12:00:00', 'dd-mm-yyyy hh24:mi:ss'), 20, 5, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (808, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/714178532002619', 'Trân trọng cảm ơn anh/ chị và các bạn đã tới tham dự buổi Dialogue 6 về chủ đề Bảo Mật vào trưa nay, ngày 10/10/2014 với 30 thành viên CIO tham gia.Hẹn gặp lại anh/ chị và các bạn vào buổi offline gần nhất trong tháng 10 này tiếp tục về chủ đề bảo mật.See you soon :-)Không khí tại buổi Dialogue 6 về chủ đề Bảo Mật tại Ân Nam Cafe do cộng đồng CIO tổ chức!', to_date('10-10-2014 15:03:00', 'dd-mm-yyyy hh24:mi:ss'), 21, 0, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (809, 446, 'https://www.facebook.com/groups/ciovietnam/permalink/713727055381100', 'Thế nào là một chiến lược an ninh công nghệ thông tin toàn diện của một doanh nghiệp; các doanh nghiệp Việt Nam đã và đang bỏ qua những khâu nào trong công tác bảo mật cho hệ thống công nghệ thông tin; doanh nghiệp nên chọn cách tiếp cận bảo mật như thế nào… là những nội dung được đề cập đến trong cuộc trao đổi của Thời báo Vi tính Sài Gòn với ông Nguyễn Bá Quỳnh, Giám đốc Bộ phận Phần mềm, IBM Việt Nam.http://www.tbvtsg.com.vn/show_article.php?id=19006&ln_id=190', to_date('09-10-2014 19:07:00', 'dd-mm-yyyy hh24:mi:ss'), 4, 0, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (810, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/713001355453670', 'ỨNG DỤNG CNTT VÀO CUỘC SỐNGĐại diện GrabTaxi tiết lộ: “Chúng tôi đã có thống kê rằng những tài xế taxi tham gia sử dụng GrabTaxi có thu nhập tăng thêm ít nhất 30% và có người còn tăng 300%”… Đó là một con số khá ấn tượng sau khi GrabTaxi vào thị trường Việt Nam dịp tháng 2/2014 ở TP.HCM và ra Hà Nội vào tháng 5. “Tốc độ tăng trưởng về người dùng của GrabTaxi cũng đang rất khả quan, khoảng 50% mỗi tháng”, đại điện GrabTaxi cho biết thêm.http://ictnews.vn/…/grabtaxi-giup-cho-cac-tai-xe-tang-thu-n…', to_date('08-10-2014 08:40:00', 'dd-mm-yyyy hh24:mi:ss'), 11, 0, 0);

