insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (610, 234, 'https://www.facebook.com/groups/ciovietnam/permalink/1205413949545739', '"BẠN CÓ BIẾT CHI PHÍ CNTT 2016 CỦA MỸ CHIẾM 1/3 CHI PHÍ CNTT TOÀN TOÀN CẦUVẫn biết chi phí cho CNTT của Mỹ là rất khủng, nhưng cũng không thể tưởng tượng được nó chiếm tới 1/3 chi phí CNTT của toàn thế giới. Đúng là Mẽo có khác, to những cái đáng to đem lại bao việc làm cho đân, không như xứ ta toàn to nhất những thứ chả đem lại giá trị gì cho dân.Theo dự báo của Gartner 2Q16 về chi phí dành cho CNTT 2016 toàn cầu lên tới 3.41 nghìn tỷ USD, không tăng so với 2015.2016 Spending 2016 Growth
Data Center Systems 174,578 2.0%
Software 332,207 5.8%
Devices 627,235 -5.3%
IT Services 897,634 3.7%
Communications Services 1,380,782 -1.4%
Overall IT 3,412,436 0.0%Bốn quốc gia dẫn đầu chi phí cho CNTT năm 2016 lần lượt là Mỹ, Trung Quốc, Nhật và Anh. Mỹ đứng đầu với chi phí chiếm 32.4%, Anh đứng thứ 4 chiếm 5.3% chi phí CNTT toàn cầu. Với việc Anh ra khỏi EU thì người ta lo ngại chi phí cho CNTT của Anh sẽ sụt giảm.Tóm lại các công ty CNTT của Việt Nam muốn phát triển có danh thu lớn nên phát triển kinh doanh sang thị trường Mỹ, và mảng IT services và Communications Services là những mảng dịch vụ hết sức bèo bở."', to_date('18-08-2016 16:56:00', 'dd-mm-yyyy hh24:mi:ss'), 4, 1, 0);

