insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (613, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/1198507156903085', '"Thân mời anh/ chị và các bạn tham dự Talkshow lần thứ 54 với chủ đề """ Chúng ta đã sẵn sàng cho một kỷ nguyên mới- hệ thống lưu trữ dữ liệu dùng toàn ổ cứng Flash""" do cộng đồng CIO tổ chứcThời gian: Từ 18:00 đến 21:00, thứ 5 ngày 11/08/2016Địa điểm: Eastin Grand Hotel Saigon, 253, Nguyễn Văn Trỗi, HCMCộng đồng CIO rất mong được đón tiếp anh/ chị và các bạn!CHÚNG TA ĐÃ SẴN SÀNG CHO MỘT KỶ NGUYÊN MỚI- HỆ THỐNG LƯU TRỮ DỮ LIỆU DÙNG TOÀN Ổ ĐĨA FLASHNgày nay, những doanh nghiệp hoạt động nhiều năm hoặc trong lĩnh vực như ngân hàng, tài chính, Game, phân tích dữ liệu và sản xuất dữ liệu ngày càng nhiều. Không gian lưu trữ và tốc độ xử lý dữ liệu để phục vụ hoạt động kinh doanh, tạo lợi thế cạnh tranh trên thị trường đang là một thách thức đối với các CIO/ CEO/ IT manager.Một CIO/ CEO/ IT Manager sẽ làm như thế nào để đáp ứng được yêu cầu thực tế này thông qua việc lựa chọn công nghệ lưu trữ, hoạnh định tài chính và vận hành hệ thống lưu trữ dữ liệu trong doanh nghiệp một cách hiệu quảTalkshow lần thứ 54 do cộng đồng CIO tổ chức xoay quanh chủ đề “ Chúng ta đã sẵn sàng cho một kỷ nguyên mới- hệ thống lưu trữ dữ liệu toàn ổ đĩa Flash”, một chủ đề chia sẻ đột về công nghệ lưu trữ kết hợp giữa hệ thống phần cứng, công nghệ đĩa Flash với kiến trúc phần mềm xử lý dữ liệu hay còn gọi là “Software Defined Storage” để đáp ứng yêu cầu không gian lưu trữ nhỏ, tốc độ xử lý dữ liệu nhanhThân mời anh/ chị và các bạn quan tâm tới chủ đề trên tham dự Talkshow lần thứ 54 tại https://goo.gl/forms/bqmXiRGBdIrbYCko2 hoặc https://goo.gl/forms/bqmXiRGBdIrbYCko2Chia sẻ tại Talkshow lần này do Ms Genevieve Goh- Phụ trách Parter của Pure Storage về công nghệ lưu trữ và những thảo luận từ thực tế từ Anh Nguyễn Phúc Nguyên- CIO của chứng khoán Bảo Việt và anh Đào Đức Luận- CIO của HDSAIGON (HD Finance) do anh Nguyễn Chí Đức- Phó chủ tịch Cộng đồng CIO Vietnam làm host.Thời gian: Từ 18:00 đến 21:00, t"', to_date('09-08-2016 09:16:00', 'dd-mm-yyyy hh24:mi:ss'), 13, 0, 0);

