values (707, 457, 'https://www.facebook.com/groups/ciovietnam/permalink/966395436780926', 'Cho hỏi anh/em CIOLần cuối cùng anh/em CIO trình bày toàn cảnh về giải pháp công nghệ áp dụng cho doanh nghiệp của anh/em cho các vị C-level cách đây bao lâu?', to_date('31-05-2015 14:35:00', 'dd-mm-yyyy hh24:mi:ss'), 0, 0, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (708, 249, 'https://www.facebook.com/groups/ciovietnam/permalink/965106006909869', 'Hôm qua trong buổi Talkshow 48, diễn giả và cộng đồng trao đổi khá nhiều về việc IT nói sao cho sếp non-IT hiểu. Mình thấy có 1 cách rất hiệu quả là tìm các ví dụ tương tự nhưng thật gần gũi với người nghe để nói.Ví dụ, anh Dưa Leo nói: Nếu bạn vào một nhà hàng ngay trong ngày khai trương của nó, kêu món chính của mà nó quảng cáo om sòm, nhưng nhà hàng lại... đi mua món đó chứ không phải tự nấu, bạn sẽ thấy thế nào?', to_date('29-05-2015 10:18:00', 'dd-mm-yyyy hh24:mi:ss'), 14, 31, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (709, 640, 'https://www.facebook.com/groups/ciovietnam/permalink/964187307001739', '"[TUYỂN BẠN ĐỒNG HÀNH DỰ ÁN KHỞI NGHIỆP]
[MẢNG: DU LỊCH - KHỞI NGHIỆP - CÔNG NGHỆ]
Admin cho mình post tí nhé! Đừng ban đừng kick :''( Có sai lầm gì thì nhắc nhở để sửa!!!
Tình hình là sau 3 đợt tuyển và với gần 40 thành viên hiện tại chạy ổn nhưng cần thêm để phát triển, các bạn nhìn giới thiệu về dự án sẽ hiểu :)
Kể cả part time, fulltime vs cộng tác viên!!!
Bọn mình cần tuyển thêm nhiều vị trí sau: COO, quản lý nhân sự, xây dựng và phát triển văn hóa công ty, Phượt thủ (backpacker), đầu bếp, RADIO MC, coder web, coderr app, các vị trí marketing (event, social, seo) CONTENT WRITER, PRESENTOR, cameraman, graphic design, editor, ux/ui, radio mc...
Mọi người ai có hứng thú thì xem chi tiết ở đây nhé ^^! http://goo.gl/XIDnvf
Có thể hỏi trực tiếp với mình ở tại note tuyển này: https://goo.gl/rCp71p
#VietnamTuiRecruit #Vietnamtui"', to_date('26-05-2015 21:27:00', 'dd-mm-yyyy hh24:mi:ss'), 0, 3, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
