insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (711, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/964369876983482', '"QUẢN TRỊ & CNTT: Liệu có vui vẻ?Dạ, vui :-)Quản trị và CNTT thường không có giới hạn và đổi mới mỗi ngày, có niềm vui nào hơn khi khám phá những điều mới mẻ như thế? Những điều mới, bất ngờ luôn rất thú vị, thật í :-) Vậy làm thế nào để vui vẻ được những điều mới mẻ đó?Cộng đồng CIO trân trọng kính mời anh/ chị/ và các bạn tham dự buổi Talkshow 48 xoay quanh chủ đề rất vui vẻ và thực tế này do anh Vu Thai Ha, tác giả cuốn sách """ Quản trị học vui vẻ""" chia sẻ những trải nghiệm thực tế cá nhân trong lĩnh vực CNTT và quản trị doanh nghiệp.Thời gian: 17h30, thứ 5, ngày 28/5/2015Địa điểm: Ân Nam Restaurant & Cafe, 52A Trương Định, Quận 3, HCMCũng tại buổi Talkshow lần này, anh Hà tặng cộng đồng CIO 40 cuốn sách """Quản trị học vui vẻ""". Toàn bộ số tiền bán 40 cuốn sách, cộng đồng CIO dùng để làm từ thiện chương trình """Từ trái tim tới trái tim lần thứ 14""" dự kiến trong tháng 6 tới. Rất mong nhận được sự ủng hộ từ các anh/ chị và các bạn!Một chủ đề hay, một diễn giả hài hước hứa hẹn một buổi chia sẻ trải nghiệm thực tế thú vị trong công việc!Hẹn gặp lại anh/ chị và các bạn tại buổi Talkshow ngày mai 28/5/2015 nha :-)"', to_date('27-05-2015 10:34:00', 'dd-mm-yyyy hh24:mi:ss'), 32, 2, 0);

