insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (714, 137, 'https://www.facebook.com/groups/ciovietnam/permalink/961791690574634', '"IT MANAGER mảng Retail !Hiện bên mình đang tuyển 01 bạn IT Manager cho mảng Retail sắp được khai trương tại Vivo CityYêu cầu:
- Có kinh nghiệm mảng Retail
- Đã từng triển khai, vận hành hệ thống ERP hoàn chỉnh, ưu tiên SAP.
- Làm việc tại Tp HCM, Q1.
- Lương, chế độ đãi ngộ hấp dẫnHồ sơ có thể PM cho mình hoặc gửi qua: dgthuy@vntic.vn.Cảm ơn nhiều."', to_date('20-05-2015 14:44:00', 'dd-mm-yyyy hh24:mi:ss'), 4, 2, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (715, 611, 'https://www.facebook.com/groups/ciovietnam/permalink/961568310596972', '"Sau buổi Talkshow 47. Em xin share bài viết với chia sẽ đầy thú vị của anh Alo Ka Ka .
Cảm ơn anh đã có bài viết khá đầy đủ về nội dung chia sẽ của anh Nguyễn Đức Quang.
https://www.facebook.com/notes/th%C3%A1nh-ph%C3%A1n-c%C3%B4ng-ngh%E1%BB%87-kinh-doanh/cio-vi%E1%BB%87t-nam-talkshow-47-x%C3%A2y-d%E1%BB%B1ng-ph%C3%A1t-tri%E1%BB%83n-v%C3%A0-chuy%E1%BB%83n-giao-th%E1%BA%BF-h%E1%BB%87-trong-doanh-n/861739707225277?__mref=message_bubbleCIO Việt Nam- Talkshow 47 “Xây dựng, phát triển và chuyển giao thế hệ trong doanh nghiệp CNTT tại Việt Nam”: Bài học phát triển doanh nghiệp"', to_date('19-05-2015 22:30:00', 'dd-mm-yyyy hh24:mi:ss'), 8, 0, 0);

