insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (717, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/959170774170059', 'Sau buổi Talkshow 47 do Cộng đồng CIO vừa qua, anh Cao C. Tran đã có những ghi nhận về việc xây dựng và quản trị doanh nghiệp CNTT của lãnh đạo kiểu mẫu.Một bài viết thật hữu ích và thực tế trong giai đoạn hiện nay.Cộng đồng CIO cảm ơn anh Nguyễn Đức Quang đã chia sẻ và bài viết của anh Công.Hẹn sớm gặp lại anh/ chị và các bạn tại Talkshow 48!Thật may mắn cho tôi khi được các anh chị cộng đồng CIO Việt Nam mời tham gia hội thảo và được một lãnh đạo như anh Nguyễn Đức Quang (chủ tịch của công ty Sao Bắc Đẩu) chia sẻ cách thức, kinh nghiệm xây dựng và chuyển giao thế hệ một doanh nghiệp CNTT. Đây là một cơ hội tốt cho những ai muốn trở thành lãnh...', to_date('14-05-2015 06:43:00', 'dd-mm-yyyy hh24:mi:ss'), 10, 0, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (718, 457, 'https://www.facebook.com/groups/ciovietnam/permalink/957670030986800', '"Khảo sát nền tảng sử dụng để phát triển ứng dụng.
Nếu (hoặc hiện nay) phát triển ứng dụng, anh/em sẽ hoặc đang lựa chọn nền tảng nào?"', to_date('10-05-2015 13:46:00', 'dd-mm-yyyy hh24:mi:ss'), 2, 2, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (819, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/702719583148514', '"Cộng đồng CIO thân mời các anh/chị tham dự buổi chia sẻ, thảo luận với chuyên đề “BUSINESS INTELLIGENCE – DOANH NGHIỆP CÓ THẬT SỰ CẦN BI?""" do anh Đào Thanh Tú – Giám đốc CNTT của Manulife Việt Nam chia sẻ, với các nội dung chính như sau:1. Khái niệm chung về BI2. Thông tin của Doanh nghiệp thường được tổ chức và vận hành như thế nào ?3. BI mang lợi ích gì cho Doanh nghiệp ? Chúng ta có thật sự cần BI ?4. Các phương pháp tiếp cận BI5. Các lưu ý khi triển khai và vận hành BI6. Hỏi đápVới kinh nghiệm triển khai BI trong thực tiễn, chắc chắn “speaker” Đào Thanh Tú sẽ có rất nhiều điều để chia sẻ với anh/chị cũng như có những câu trả lời thỏa đáng cho sự băn khoăn của các anh/chị về BI.Thân mời các anh/chị đăng ký tham dự với thông tin như sau:- Thời gian: 05:30 PM, thứ 05 ngày 25/09/2014- Địa điểm: ÂN NAM Café , 52 Trương Định, Quận 3, TP.HCM- Lịch trình:05:30 PM: Đón khách06:00PM - 08:00PM: Thảo luận, trao đổi “Business Inteligence – Doanh nghiệp có thực sự cần BI?”08:00 – 08:30PM: Giao lưuChi phí tham dự: 100,000đ / người"', to_date('16-09-2014 21:20:00', 'dd-mm-yyyy hh24:mi:ss'), 29, 2, 0);

