insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (838, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/683647535055719', '"ITLC OFFLINE: PHÂN TÍCH COST, BENEFITS & ROI CHO DỰ ÁN ITCộng đồng ITLC tổ chức buổi offline với chủ đề “Phân tích Cost, Benefits & ROI cho dự án IT” tại Sài Gòn cùng nhau chia sẻ kinh nghiệm trong quá trình làm việcRed xin chia sẻ thông tin tới anh/ chị và các bạn quan tâm cùng tham gia :-)Thời gian: 9h-11h30, thứ bảy ngày 9/8/2014
