insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (841, 256, 'https://www.facebook.com/groups/ciovietnam/permalink/678226255597847', 'Các anh em có kinh nghiệm về thanh toán online cho hỏi với. Mình muốn tích hợp cổng thanh toán online vào website bán lẻ. Đáp ứng thanh toán thẻ nội địa và quốc tế nhưng phải tiện dụng cho người dùng, người dùng không cần phải đăng ký account hoặc deposit tiền cho đơn vị cung cấp dịch vụ cổng thanh toán mà chỉ cần nhập Tên, số thẻ, mã PIN là thanh toán được (kiểu như thanh toán online của Visa và Master vậy). Những hệ thống nào đáp ứng được y/c đó vậy các chiến hữu ơi? Thanks', to_date('26-07-2014 17:10:00', 'dd-mm-yyyy hh24:mi:ss'), 6, 12, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (842, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/673390099414796', '"OFFLINE CHỦ ĐỀ ITIL/ ITSM TẠI SÀI GÒNCộng đồng ITLC là cộng động hoạt động trong lĩnh vực công nghệ thông tin, thường xuyên tổ chức các buổi offline chia sẻ nhiều kinh nghiệm quản lý, quản trị và trao đổi trong công việcThời gian gần đây, nhiều doanh nghiệp đã bắt đầu triển khai ITIL/ ITSM. Mối liên hệ giữa ITIL và ITSM như thế nào? những khó khăn khi triển khai ra sao? Doanh nghiệp sẽ phải chuẩn bị những gì để triển khai dự án ITIL trong doanh nghiệpTất cả sẽ được trao đổi tại buổi offline vào thứ bảy tuần này,
