insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (618, 177, 'https://www.facebook.com/groups/ciovietnam/permalink/1161673237253144', 'You are invited to the VEMBA Talk Series "Innovation & Entrepreneurship" #1.The talk speaker - Dr Westland is the author of numerous academic papers and of seven scholarly books: Financial Dynamics (Wiley2003); Valuing Technology (Wiley 2002); Global Electronic Commerce (MIT Press 2000); Global Innovation Management(Palgrave-Macmillan 2008, 2nd ed 2015); Red Wired: China’s Internet Revolution (Marshall Cavendish, 2010); Structural Equation Modeling (Springer 2015), and Financial Accounting with Information Technology (CreateSpace 2014).NO ATTENDANCE FEE BUT REGISTRATION IS REQUIRED!', to_date('10-06-2016 23:12:00', 'dd-mm-yyyy hh24:mi:ss'), 3, 0, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (619, 234, 'https://www.facebook.com/groups/ciovietnam/permalink/1169936729760128', '"Cách mà IT kiếm tiền cho doanh nghiệpVới các nhà quản tri doanh nghiệp mục tiêu 37% các dự án IT để tiết kiệm tiền cho doanh nghiệp, nhưng có tới 63% các dự án IT là kiếm tiền cho doanh nghiệp.Đối với doanh nghiệp Việt Nam phần lớn chỉ thấy dự án IT tiêu tiền của doanh nghiệp chứ không phải là kiếm tiền, và các dự án IT tiết kiệm tiền là chi phí càng ít tiền cho IT càng tốt. Nhưng thực tế không phải thế.Vậy các CIO kiếm tiền cho doanh nghiệp bằng cách nào?
Dưới đây là kết quả khảo sát của Harvey Nash / KPMG CIO Survey 2016 với 3352 CIO trên 82 quốc gia, danh sách thứ tự ưu tiên chính hội đồng quản trị tìm kiếm ở CIO đem lại cho doanh nghiệp:1. Increasing operational efficiencies 57%
2. Improving business processes 56%
3. Delivering consistent and stable IT performance to the business 51%
4. Saving costs 50%
5. Delivering business intelligence/analytics 44%
6. Enabling business change 44%
7. Developing innovative new products and services 41%
8. Cyber security 41%
9. Driving revenue growth 40%
10. Better engagement with customers/prospects 38%
11. Managing operational risk and compliance 36%
12. Improving time to market 26%
13. Improving the success rate of projects 26%
14. Outperforming competitors with new business models 23%
15. Enabling mobile commerce 19%
16. Driving synergies from mergers & acquisitions 12%
17. Investing in social media platforms 7%
18. Reputation management via social media technology 7%
19. Achieving sustainable/green IT 7%"', to_date('24-06-2016 11:49:00', 'dd-mm-yyyy hh24:mi:ss'), 11, 1, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (620, 678, 'https://www.facebook.com/groups/ciovietnam/permalink/1170496539704147', '"Chia sẻ của anh Nguyễn Bá Thành (CEO Weplay) về xu thế lập trình mobile với các dự án start-up hiện nay. Anh là tác giả ứng dụng Đuổi Hình Bắt Chữ hơn 5 triệu lượt tải, Lịch Vạn Niên hơn 1 triệu lượt tải, hiện tại đang chạy nhiều dự án start-up.Cùng nghe chia sẻ của giảng viên Nguyễn Bá Thành về xu thế lập trình mobile với các dự án start-up ngày nay và lời khuyên dành cho các bạn học viên tương lai của lớp Android 03 sắp tới. Anh là tác giả ứng dụng Đuổi Hình Bắt Chữ hơn 5 triệu lượt tải, Lịch Vạn Niên hơn 1 triệu lượt tải, hiện tại đang chạy nhiều dự án start-up.
Học viên hoàn thành khóa học được trung tâm đảm bảo việc làm, nhu cầu tuyển dụng Android rất lớn.
______________________________________
【Lớp Android 03 - lập trình qua 10 ví dụ thực tế】Khai giảng đầu tháng 7. Hiện đã có 8 bạn đăng ký.- Đăng kí tại: https://techmaster.vn/khoa-hoc/25513/lap-trinh-android
- Link sự kiện: https://www.facebook.com/events/1566441446983611/
- 12 buổi, mỗi buổi 3 tiếng, dự án mẫu trực quan, học viên nắm bắt nhanh chóng kỹ thuật phát triển ứng dụng Android chuyên nghiệp.Call: 0902209011"', to_date('25-06-2016 10:18:00', 'dd-mm-yyyy hh24:mi:ss'), 1, 0, 0);

