insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (733, 666, 'https://www.facebook.com/groups/ciovietnam/permalink/936615786425558', '"Với xu hướng Social, Mobility, Analytics/ Big Data, Cloud đang phát triển với tốc độ chưa từng có, Internet of Everything cùng với các cơ hội lớn đối với cá nhân, doanh nghiệp, tổ chức, quốc gia, khu vực như thế nào?Cộng đồng CIO thân mời anh/ chị và các bạn tham dự buổi Talkshow lần thứ 46 với chủ đề """Internet của vạn vật- Internet of everything: Cơ hội, mô hình kinh doanh, ứng dụng, giải pháp trong thực tế""" do ông Phan Thanh SƠn- Chuyên gia Chuyên nghệ thông tin- Truyền thông, người đã có 22 năm kinh nghiệm về CNTT- TT trong nhiều lĩnh vực như Ngân hàng, Chính Phủ, Nhà cung cấp dịch vụ, Dầu khí, Sản xuất, Bất động sản/ Xây dựng, Quân sự với nội dung1. IOT đang phát triển và được ứng dụng mức nào ở Việt nam và trên thế giới2. Giá trị IOT mang lại cũng như ứng dụng trong cuộc sống ra sao?3. Một số thực tế triển khai IOTThời gian: 17h30 đến 19h30, thứ 5 ngày 2 tháng 4 năm 2015Địa điểm: Ân Nam Restaurant & Cafe, 52A Trương Định, Phường 7, Quận 3, HCMPhí tham dự: 100.000 VNĐ/ ngườiHẹn gặp lại anh chị và các bạn tại buổi talkshow!"', to_date('26-03-2015 13:02:00', 'dd-mm-yyyy hh24:mi:ss'), 26, 5, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (847, 446, 'https://www.facebook.com/groups/ciovietnam/permalink/668406359913170', '"Thưa các anh/ chị và các bạn,CIO Việt Nam đã có buổi viếng thăm ấm áp tại nhà tình thương chùa Phật Minh Bến Tre vào ngày 5/7/2014.Để thực hiện được chương trình này, chúng tôi đã nhận sự ủng hộ công sức, đóng góp từ anh/ chị và các bạn dành cho chuyến đi.Chúng tôi xin gửi lòng biết sâu sắc đến anh/ chị và các bạn đã chung tay góp sức cho chương trình.Cầu Chúc an lành đến với các anh/ chị, các bạn và mọi tấm lòng từ tâmTrân trọng cảm ơn.Thay mặt BTC CIO Việt Nam
