insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (854, 446, 'https://www.facebook.com/groups/ciovietnam/permalink/659238407496632', '"Nhằm nâng cao các hoạt động mang lại kinh nghiệm thực tiển cho anh em làm công nghệ.
CIO VN sẽ giới thiệu đến anh chị chuỗi sự kiện """CIO in Action""" đây là các buổi giới thiệu những hệ thống mẩu hoặc các hệ thống thực đang vận hành.Thông tin chi tiết sẽ được cập nhật trên site của CIO VN.Trân trọng.Phuc Huynh"', to_date('19-06-2014 18:06:00', 'dd-mm-yyyy hh24:mi:ss'), 43, 2, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (855, 374, 'https://www.facebook.com/groups/ciovietnam/permalink/659215920832214', 'You are invited to CIO Vietnam 34th talkshow!', to_date('19-06-2014 16:51:00', 'dd-mm-yyyy hh24:mi:ss'), 29, 3, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (856, 559, 'https://www.facebook.com/groups/ciovietnam/permalink/658223534264786', 'Các nhà quản lý công nghệ trẻ nói gì về MIS trong CIO Dialogue lần IV ngày 20/6.Trong bối cảnh doanh nghiệp đang cộng tác và trên cương vị của mình (là CIO), theo anh chị thì MIS là gì, vai trò và giá trị của MIS trong DN của mình hiện tại và kỳ vọng gì trong 3-5 năm tới?Theo anh chị thì CEO/TGĐ và BGĐ của DN có hoàn toàn (100%) đồng ý và chia sẽ quan điểm trên của anh chị không? Nói cách khác, có sự khác biệt gì trong cách nhìn nhận MIS giữa CIO và CEO?Biết người biết ta trăm trận trăm trận ít thua (SWOT): Theo đánh giá của anh chị, hệ thống MIS của các đối thủ cùng ngành có những lợi thế gì nỗi trội và thua kém gì so với MIS của DN mình?Anh chị có thể chia sẽ đôi chút về trải nghiệm của DN mình trên con đường xây dựng và phát triển MIS: Các yếu tố cần và đủ để thành công, những thuận lợi/khó khăn(thách thức) và bài học xương máu cũng như lời khuyên cho những người đi sau?Anh chị có kiến nghị./ đề xuất gì để giúp DN của mình và các DN khác nói chung, có thể xây dựng và phát triển MIS thành công, sử dụng/ khai thác MIS hiệu quả hơn?(Môi trường/ luật pháp/ Kiểm toán, đối tác/ nhà cung cấp sản phẩm dịch vụ/ tư vấn/ đào tạo…', to_date('17-06-2014 15:33:00', 'dd-mm-yyyy hh24:mi:ss'), 15, 2, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (857, 446, 'https://www.facebook.com/groups/ciovietnam/permalink/659093354177804', 'Cần tìm một tình nguyện viên chuyên về thiết kế cho cộng đồng CIO Việt Nam. Anh chị nào có kỷ năng và nhiệt huyết xin vui lòng in box cho Huynh Phuc hoăc Linh La nhé.', to_date('19-06-2014 08:54:00', 'dd-mm-yyyy hh24:mi:ss'), 13, 0, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (858, 446, 'https://www.facebook.com/groups/ciovietnam/permalink/654528687967604', '"Khó khăn của người Quản Lý CNTT (CIO/IT Director Paint-points):
1. CEO quá bận rộn và hầu như không có thời gian cho IT, rất khó tiếp cận CEO.
Là IT Director (không báo cáo trực tiếp cho CEO) anh chị sẽ nói gì nếu có cuộc họp 1h với CEO?
Phuc tagged anh/chị là CEO/CIO để xin những lời khuyên cho ngữ cảnh nầy. (Nếu anh chị thấy phiền xin inbox nhé)
Huynh Phuc"', to_date('10-06-2014 12:06:00', 'dd-mm-yyyy hh24:mi:ss'), 39, 17, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (624, 457, 'https://www.facebook.com/groups/ciovietnam/permalink/1132940106793124', 'Giải pháp quản trị doanh nghiệp.Tựu trung các giải pháp quản trị doanh nghiệp hiện nay để cùng với các nhà quản trị của doanh nghiệp xây dựng nên vương quốc của mình sẽ bao gồm:Giải pháp ERP - một giải pháp nắm giữ toàn bộ các hoạt động nội bộ của doanh nghiệp, nhưng một back-end của hệ thống quản trị nhằm phục vụ cho toàn bộ các hoạt động của doanh nghiệp một các suông sẻ, nhanh chóng, và là người cung cấp thông tin chi tiết cho toàn bộ các hoạt động. ERP sẽ là qui trình vận hành trong nội bộ của doanh nghiệp, ERP là quản lý tồn kho, ERP là quản lý sản xuất, ERP là quản lý thu chi, ERP là tài chính kế toán, ERP là dòng tiền, ERP là báo cáo tài chính, ERP là qui trình mua hàng, ERP là hoạt động quản lý các đơn hàng của khách hàng - ERP là giám sát hoạt động của toàn bộ doanh nghiệp, ngay cả các công ty thành viênGiải pháp CRM - một giải pháp nữa theo chân nhà quản trị và khách hàng đó là "Quan hệ" với khách hàng, CRM là tác động qua lại lẫn nhau giữa doanh nghiệp và khách hàng, CRM là nhận thức lẫn nhau giữa khách hàng và doanh nghiệp, giúp cho nhà quản trị định vị được vị trí của mình đối với khách hàng, xác định vị trí của doanh nghiệp ở đâu trong chiến lược phát triển của khách hàng và chắc chắc là cũng xác định được khách hàng ở đâu trong chiến lược của doanh nghiệp bạn - CRM là marketing, CRM là kinh doanh, và rất quan trọng CRM là dịch vụ khách hàng. CRM sẽ là người cho bạn biết được khách hàng của bạn đang cần gì, và làm thế nào để nắm giữ khách hàng của mình lại.Giải pháp BI - Với bối cảnh kinh tế hiện nay là kinh tế phẳng, nền kinh tế tri thức và siêu cạnh tranh buộc các nhà lãnh đạo luôn luôn phải có thông tin, và các giải pháp quản trị cần phải cung cấp cho họ các góc nhìn, nếu không muốn nói là mọi góc nhìn về doanh nghiệp của bạn và thế giới xung quanh - một big picture sẽ giúp cho nhà lãnh đạo đặt doanh nghiệp của bạn vào các workplace thinking, xác định', to_date('23-04-2016 10:08:00', 'dd-mm-yyyy hh24:mi:ss'), 8, 0, 0);

