insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (630, 674, 'https://www.facebook.com/groups/ciovietnam/permalink/1092052764215192', 'Dear admin group & các bạn, cho mình đăng tin tuyển dụng. Mo ta công việc: Hỗ trợ người dùng các ứng dụng MS office, client email, ERP, Workstation PC, PBX, PBA. Van hành mạng LAN, WAN, WLAN, VPN, Internet với các thiết bị cisco, Fortinet... Và quản lý các dịch vụ AD, NAS, Backup, Anti virus... Noi làm việc KCN Ba'' Thiện 2, Bình Xuyên, Vĩnh Phúc. Yêu cầu bắt buộc: Giao tiếp được bằng English. Ưu tiên các bạn ở gần KCN Bình Xuyên hoặc xác định cư ở gần công ty và xác định làm việc lâu dài. Độ tuổi sinh từ 1987 đến 1994. Công ty sẽ đào tạo thêm các kỹ năng và chuyên môn. Mức lương cơ bản từ 6 - 8 triệu. Các bạn có nhu cầu vui lòng inbox. Trân trọng cám ơn!', to_date('23-02-2016 10:16:00', 'dd-mm-yyyy hh24:mi:ss'), 2, 0, 0);

