insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (633, 25, 'https://www.facebook.com/groups/ciovietnam/permalink/1090608241026311', 'Hi all,Sắp tới đội Oracle của nước ngoài về Việt Nam và đang mong muốn rót fund cho bên mình tổ chức một seminar nhỏ so sánh giữa MySQL Community và Enterprise, ko biết liệu có anh em nào quan tâm về mảng này ko nhỉ ?Hay MySQL community đã đủ dùng ?Nhờ mọi người góp ý nhé.', to_date('20-02-2016 22:29:00', 'dd-mm-yyyy hh24:mi:ss'), 10, 0, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (634, 538, 'https://www.facebook.com/groups/ciovietnam/permalink/1069942419759560', '"Anh chị em nào có, hoặc biết 2 giải pháp sau:
