insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (640, 630, 'https://www.facebook.com/groups/ciovietnam/permalink/1063394247081044', 'ACE có kinh nghiệm phát triển phần mềm sử dụng freelancer cho mình hỏi. Nếu thuê freelancer, trong quá trình viết code thì cũng cần phải test trên máy localhost, mà để hệ thống vận hành được thì máy phải có đầy đủ source code. Như vậy thì làm thế nào để bảo vệ bí mật thông tin cũng như bản quyền của phần mềm?', to_date('29-12-2015 11:26:00', 'dd-mm-yyyy hh24:mi:ss'), 4, 6, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (641, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/1060778477342621', '"CIO 5 năm một hành trình là một sự kiện do cộng đồng CIO tổ chức lớn nhất từ trước tới nay nhân dịp kỷ niệm 5 năm phát triển cộng đồng CNTT. Trong sự kiện lần này có buổi Tọa đàm """ Trải nghiệm không giới hạn""" với 4 chủ đề khác nhau, xin giới thiệu với anh/ chị và các bạn chủ đề thứ nhất""" Trải nghiệm sự thất bại: Bài học AZ Solution JSC"""cộng đồng CIO thân mời anh/ chị và các bạn tham dự vào ngày 14/1/2016."', to_date('25-12-2015 09:21:00', 'dd-mm-yyyy hh24:mi:ss'), 22, 2, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (642, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/1057119837708485', '"Kỷ niệm 5 năm một hành trình phát triển cộng đồng CIO Việt Nam được tổ chức vào ngày 14/1/2016Thân mời anh/ chị và các bạn tham gia tọa đạm """ TRẢI NGHIỆM KHÔNG GIỚI HẠN""" được tổ chức vàoThời gian: 18h00 đến 22h30 ngày 14/1/2016Địa điểm: Khách sạn Riverside PlaceCộng đồng CIO Vietnam rất hân hạnh được đón tiếp anh/ chị và các bạn tham dự sự kiện đánh dấu chặng đường 5 năm đã đi qua và nhiều dự định sắp tới"', to_date('18-12-2015 13:26:00', 'dd-mm-yyyy hh24:mi:ss'), 20, 2, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (643, 234, 'https://www.facebook.com/groups/ciovietnam/permalink/1059029844184151', '"3 kiểu CIO điển hình.Business Strategist CIO
Transformation CIO
Function Head CIOMột bức tranh rất rõ nét giúp các CIO định hình chức năng nhiệm vụ của mình. Tuy nhiên rất ít các CIO chỉ đảm nhận một chức năng ở trên, thường phải đảm nhận cả 3 chức năng, chỉ là tỷ trọng dành phần lớn thời gian cho chức năng nào mà thôi.Hiện tại CIO dành nhiều thời gian đảm nhận công việc chính là “Function Head”. Tiếp đó là thời gian giành cho chức năng “Transformation CIO”. Và rất ít thời gian cho chức năng “Business Strategist CIO.Với yêu cầu của môi trường kinh doanh hiện tại và trong tương lai CIO cần dành nhiều thời gian hơn để tham gia vào việc hoạch định chiến lược kinh doanh, dùng IT tạo lợi thế cạnh tranh thông qua đổi mới trong và ngoài doanh nghiệp.At this year’s CIO Summit in Sydney, Genea CIO and Director of the CIO Executive Council, Peter Nevin, presented on the evolution of the CIO role. He introduced the three CIO types: function head, transformational leader and business strategist or ‘future-state’ CIO. Based on your current primary activities, which archetype best describes you?Three CIO Types Primary ActivitiesBUSINESS STRATEGIST
Creates/enables business strategy & competitive advantage through innovation
across and external to the enterpriseTRANSFORMATIONAL LEADER
Leverages alignment & close partnership with the business to create enterprise change and optimize processesFUNCTION HEAD
Runs the IT organization, achieving IT operational excellence & providing effective, reliable servicesMore information:
http://demo.idg.com.au.s3.amazonaws.com/…/Evolution_Of_The_…"', to_date('22-12-2015 11:26:00', 'dd-mm-yyyy hh24:mi:ss'), 0, 0, 0);

