insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (741, 446, 'https://www.facebook.com/groups/ciovietnam/permalink/773040736116398', 'Bên ngoài là những cây củi to, cao, buộc chụm lại như hình cái tháp. Bên trong là những cây củi nhỏ xếp thành đống. Người ta châm lửa vào đống củi nhỏ bên trong, gió được hút vào qua các khe giữa các cây củi to làm ngọn lửa bốc cao, cháy rừng rực. Một cảnh tượng cực đẹp mà những ai được chứng kiến chắc chắn sẽ không bao giờ quên.Và ngọn lửa cũng là một phần quan trọng trong biểu tượng của CIO Việt Nam. Bốn năm trước, đốm lửa CIO Việt Nam đã được châm lên. Giờ đây, bó củi nhỏ CIO Việt Nam đang bùng cháy hết mình với hơn 30 anh chị em ban tổ chức cực kỳ hăng hái, nhiệt tình. Ngọn lửa của CIO Việt Nam cũng đã lan tỏa tới hơn 4000 members thông qua 42 Talkshow, 11 Dialog và 12 chương trình từ thiện.Trong năm 2015 này, ngọn lửa CIO Việt Nam sẽ bốc cháy cao hơn nữa, chiếu sáng hơn nữa và lan tỏa hơn nữa với những chương trình, những sự kiện đang được lên kế hoạch. Để đạt được điều đó, sự tham dự, đóng góp của các anh chị em là một phần không thể thiếu. Hãy cùng bùng cháy với CIO Việt Nam. Để chúng ta cùng là một phần của một điều lớn lao hơn. Hãy đem lại những ngọn gió tiếp sức cho ngọn lửa CIO Việt Nam. Để chúng ta cùng có những trải nghiệm không bao giờ quên trong cuộc đời', to_date('21-01-2015 19:45:00', 'dd-mm-yyyy hh24:mi:ss'), 40, 1, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (742, 374, 'https://www.facebook.com/groups/ciovietnam/permalink/926138107473326', 'Tại sao các dự án CNTT của Việt Nam hay có vấn đề hoặc thất bại, trong khi đó các dự án triển khai ở các công ty nước ngoài lại có thể chuyển giao thành công. Bí quyết nằm ở đâu, lên kế hoạch chia sẻ kinh nghiệm thực tế và các bí quyết quản lý dự án thành công thôi. Bác nào quan tâm thì PM nhé, dự kiến mình sẽ trao đổi và hướng dẫn trong khoang 6 tiếng đồng hồ (1 ngày)!', to_date('06-03-2015 14:35:00', 'dd-mm-yyyy hh24:mi:ss'), 42, 45, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (743, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/929322660488204', 'TALKSHOW 45st: CHIA SẺ KINH NGHIỆM QUẢN TRỊ DỰ ÁNĐến hẹn lại lên, cộng đồng CIO thân mời anh/ chị và các bạn tham dự buổi talkshow 45 (talkshow đầu tiên năm 2015) chia sẻ kinh nghiệm quản trị dự án trong lĩnh vực CNTT do ông Paul Cornhill, CEO- Computime APAC với 20 năm kinh nghiệm chia sẻKhi tham dự buổi Talkshow lần này, ngoài việc review lại những mô hình, quy trình quản trị dự án cơ bản, ông Paul còn chia sẻ những yếu tố quyết định thành công một dự án mà các quản trị dự án nhất định không thể bỏ qua bên cạnh rất nhiều yếu tố khác. Ngoài ra tất cả những vướng mắc anh/ chị gặp phải trong quá trình làm việc có thể trao đổi với diễn giả đề tìm ra hướng đi trong thời gian tới.Đối tượng nên tham gia: Anh/ chị và các bạn đang làm PM tại các công ty IS, Software hoặc định hướng phát triển nghề nghiệp trở thành Project Manager.Số lượng: Chỉ 25 thành viên!Hy vọng nội dung chia sẻ đầu năm này mang lại nhiều thông tin giá trị tới tất cả các thành viên trong cộng đồng CIOHẹn gặp lại anh/ chị và các bạn vào thứ 7 ngày 21/3/2015 tại địa điểm quen thuộc Ân Nam Restaurant & Cafe!', to_date('11-03-2015 15:16:00', 'dd-mm-yyyy hh24:mi:ss'), 26, 15, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (744, 633, 'https://www.facebook.com/groups/ciovietnam/permalink/930377990382671', 'Internet of Thing đang là xu hướng công nghệ toàn cầu trong tương lai không xa, vậy các CIO doanh nghiệp của ta đang có những phương pháp luận, tâm thế gì để chào đón xu hướng này?', to_date('13-03-2015 09:46:00', 'dd-mm-yyyy hh24:mi:ss'), 4, 3, 0);

