insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (46, 318, 'https://www.facebook.com/groups/ciovietnam/permalink/2418171651603290', '"Bà con cô bác bạn bè thân hữu anh chị gần xa nào có vé máy bay cũ/mới gì chụp lại cho mình/cháu/em/anh xin chục cái ảnh của mỗi vé nhé ( 10 ảnh/1 vé, chụp với góc hoặc nền khác nhau).
