insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (237, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/2190641264356331', '"Các bạn tham gia chương trình """Hạt giống lãnh đạo CNTT""" năm 2018 đã chia sẻ những gì sau khi tham gia?Mời anh/ em cùng nghe những chia sẻ ấy từ bạn Kỳ Huỳnh để cùng hiểu thêm về chương trình.CÀ PHÊ Ư…TIỀN Ư… LI DỊ Ư? KHÔNG PHẢI ĐÂU!Sau một ngày dài xoăn tít não, đầu mình hoàn toàn trống rỗng khi ngồi gõ những dòng này. Thật đấy! nó trống trơn sau những nỗ lực “bóc tách“ các process rối như canh hẹ của cái dự án to oạch mà mình đang xử lý ra khỏi suy nghĩ.Có thể bạn sẽ tò mò, làm chi để mà vất vả thế?!Ồ…để viết! vâng, để viết đấy! viết một ít dòng chia sẻ về chương trình “HẠT GIỐNG LÃNH ĐẠO CNTT“, một chương trình huấn luyện gần như là khắc nghiệt nhất mà bạn không thể tìm thấy cái tương tự ở VN! Và dĩ nhiên là nó chả liên quan gì tới tiền, cà phê hay là vụ lùm xùm của vợ chồng nhà anh Vũ mấy hôm nay! Nhưng nếu bạn đang nghe về nó, hãy dừng lại vài giây, biết đâu sẽ là một bước ngoặc để bạn thay đổi cả tương lai. Trustme!Hàng năm, chương trình này sẽ tìm kiếm, chọn lọc để huấn luyện một số lượng rất giới hạn những ứng viên trong vòng 7 - 8 tháng. Mục tiêu là sau khi kết thúc chương trình, các ứng viên sẽ trưởng thành, có đầy đủ uy tín, kiến thức, kỹ năng mối quan hệ và tâm thế để trở thành một nhà lãnh đạo công nghệ thông tin trong tương lai.Nghe cũng vui vui tai đúng không? mình là một trong 12 hạt giống của chương trình huấn luyện năm 2018.Nếu bạn biết Kỳ Huỳnh đủ lâu để thấy những status chém gió tung bụi mù bất chấp sợ hãi, những bài viết khiến bạn phải bật cười về độ bành trướng và tản mác của kiến thức cách đây gần 1 năm. Mình tin rằng bạn sẽ lờ mờ nhận ra có điều gì đó đã khác đi trong khoảng thời gian gần đây.Một Kỳ Huỳnh cẩn trọng, biết ít đi, biết sâu và quan trọng hơn hết: một kẻ đủ để được gọi là trưởng thành để tự dẫn dắt bản thân mình trước khi đi lãnh đạo người khác.TĨNH LẶNG, CÚI ĐẦU ĐỂ KHIÊM NHƯỜNG VÀ MẠNH MẼ.Không có quá nhiều người có đủ tâm, đủ "', to_date('22-02-2019 05:59:00', 'dd-mm-yyyy hh24:mi:ss'), 21, 0, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (238, 13, 'https://www.facebook.com/groups/ciovietnam/permalink/2189064967847294', '18 years of tech disruption in 60 seconds!See how Tech Companies #Apple #Google #Amazon have conquered the top four spot from traditional brands from 2000 - 2018Amazon grew the fastest - see last few seconds!#HP was knocked off the list, so was #Nokia #GE #Cisco #Intel and so many giants of the 2000 era.A stark reminder for incumbents - If you don''t innovate, you''ll die eventually and a great opportunity for the #AI economy - your humble #MachineLearning #startup today could rule the game in the next decade!Source: Data Value#brands #disruption #technologytrends #deeplearning', to_date('21-02-2019 07:44:00', 'dd-mm-yyyy hh24:mi:ss'), 8, 1, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (56, 622, 'https://www.facebook.com/groups/ciovietnam/permalink/2411135762306879', 'Những thách của các doanh nghiệp trong Thời đại sốTrong Thời đại số ngày nay, Người tiêu dùng và Doanh nghiệp dễ dàng tiếp cận được thông tin của nhau nhiều hơn thông qua các các kênh Digital như Facebook, Website, Email Marketing,... Điều này vừa mang lại lợi ích nhưng cũng gây cho doanh nghiệp nhiều thách thức "đau đầu" từ nội bộ doanh nghiệp đến thị trường bên ngoài doanh nghiệp (khách hàng, đối thủ cạnh tranh, thị trường,…). Vậy doanh nghiệp sẽ gặp những thác thức gì trong thời đại số hôm nay?Sự kháng sự của doanh nghiệp với việc thay đổi.Thiếu tầm nhìn rõ ràng đối với hành trình của khách hàngViệc thu thập và tận dụng dữ liệu khách hàng không hiệu quảQuy trình phát triển và quản lý công nghệ không linh hoạtCó sự liên kế với mô hình kinh doanh kế thừaĐây là những vấn đề mỗi doanh nghiệp có thể gặp phải trong quá trình hoạt động. Vậy làm thế nào có thể vượt qua được những thách thức đó. Mời anh/chị tham gia sự kiện với chủ đề “Chiến lược Kinh doanh Thời Đại Số” kết hợp giữa Hội Hàng Việt Nam Chất lượng Cao và CIO Vietnam cùng tổ chức.-----------------------------------------------------------Thời gian: 8h30 – 11h00, thứ Năm, ngày 18/07/2019Địa điểm: Tầng 12, IMC Building, 62 Trần Quang Khải, Phường Tân Định, Quận 1, HCM.Thông tin chi tiết về chương trình và đăng kí tại đây: http://ciovn.org/chien-luoc-kinh-doanh-thoi-dai-so/  ', to_date('14-07-2019 09:42:00', 'dd-mm-yyyy hh24:mi:ss'), 9, 0, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (63, 641, 'https://www.facebook.com/groups/ciovietnam/permalink/2401025856651203', '"Cộng đồng CIO Vietnam trân trọng cảm ơn hơn 120 anh chị là các CEO, CIO, Product Manager, IT Manager,.. đã đến tham dự chương trình """We share together""" với chủ đề """Design Thinking - Tư duy giải pháp đột phá""" vào ngày 6/7/2019 tại Én Tea House and Restaurant.Đồng thời, cộng đồng cũng xin cảm ơn tới 3 vị diễn giả là anh Tài Lê, chị Hạnh Ngô và anh Châu Đinh đã chia sẻ những kinh nghiệm trong việc ứng dụng Design Thinking để giải quyết vấn đề và phát triển sản phẩm cũng như xử lý các vấn đề khác.Bên cạnh đó, cảm ơn những tình nguyện viên đã đồng hành cùng CIO Vietnam tại chương trình lần này và mong rằng sẽ đồng hành cùng mọi người trong những chương trình tiếp theo.Sắp tới đây, vào ngày 18/7/2019, cộng đồng CIO Vietnam cùng Hội doanh nghiệp chất lượng cao tổ chức chuỗi chương trình """Digitalization for SMEs""", trong đó sự kiện đầu tiên với chủ đề """Chiến lược kinh doanh thời đại số""".Thông tin chi tiết về chương trình và link đăng ký mời anh chị tham khảo tại đây: http://bit.ly/2XtJ7Q8CIO Vietnam Team <3Cộng đồng CIO Vietnam trân trọng cảm ơn hơn 120 anh chị là các CEO, CIO, Product Manager, IT Manager,.. đã đến tham dự chương trình """We share together""" với chủ đề """Design Thinking - Tư duy giải pháp đột phá""" vào ngày 6/7/2019 tại Én Tea House and Restaurant.Đồng thời, cộng đồng cũng xin cảm ơn tới 3 vị diễn giả là anh Tài Lê, chị Hạnh Ngô và anh Châu Đinh đã chia sẻ những kinh nghiệm trong việc ứng dụng Design Thinking để giải quyết vấn đề và phát triển sản phẩm cũng như xử lý các vấn đề khác.Bên cạnh đó, cảm ơn những tình nguyện viên đã đồng hành cùng CIO Vietnam tại chương trình lần này.Sắp tới đây, vào ngày 18/7/2019, cộng đồng CIO Vietnam cùng Hội doanh nghiệp chất lượng cao tổ chức chuỗi chương trình """Digitalization for SMEs""", trong đó sự kiện đầu tiên với chủ đề """Chiến lược kinh doanh thời đại số"""Thông tin chi tiết về chương trình và link đăng ký mời anh chị tham khảo"', to_date('08-07-2019 12:54:00', 'dd-mm-yyyy hh24:mi:ss'), 22, 1, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (64, 696, 'https://www.facebook.com/groups/ciovietnam/permalink/2400925936661195', '"Cuộc cách mạng số đang tạo ra nhiều thay đổi trong môi trường kinh doanh với việc đẩy mạnh tương tác online, tích hợp và giao thoa với các kênh truyền thống (offline). Doanh nghiệp đang được đặt vào một “bài kiểm tra” về năng lực và sự nhanh nhạy trong cuộc chơi mới, cuộc chơi số hoá. Trong đó, “người tiêu dùng” là câu trả lời then chốt. Hành vi tiêu dùng thời đại số đã và đang thay đổi từng ngày nhưng doanh nghiệp đã có đủ sự sẵn sàng để hiểu và cung cấp hành trình khách hàng tốt nhất hay không? Chiến lược kinh doanh thời đại số phải thay đổi gì để phù hợp?

Hiểu các băn khoăn chung của doanh nghiệp, đặc biệt là doanh nghiệp nhỏ và vừa, Hội Doanh nghiệp Hàng Việt Nam Chất Lượng Cao phối hợp cùng cộng đồng CIO Vietnam tổ chức chuỗi chương trình #DigitalizationforSMEs, trong đó sự kiện đầu tiên sẽ có chủ đề “Chiến lược kinh doanh thời đại số” với mục tiêu chia sẻ về:

✔ Nhận dạng chân dung người tiêu dùng hiện đại
✔ Hành vi tiêu dùng thời đại số đã khác như thế nào so với trước đây?
✔ Chiến lược kinh doanh thay đổi như thế nào trong thời đại số
✔ Các câu chuyện thực tế và các chia sẻ kinh nghiệm

Đăng ký online tại: http://ciovn.org/chien-luoc-kinh-doanh-thoi-dai-so/"', to_date('08-07-2019 11:17:00', 'dd-mm-yyyy hh24:mi:ss'), 6, 0, 0);

