values (203, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/2212117948875329', 'Mối quan hệ giữa #CIO & #CMO', to_date('09-03-2019 08:26:00', 'dd-mm-yyyy hh24:mi:ss'), 15, 4, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (262, 641, 'https://www.facebook.com/groups/ciovietnam/permalink/2155153301238461', 'NHỮNG CHỦ ĐỀ TRONG CHƯƠNG TRÌNH "HẠT GIỐNG LÃNH ĐẠO CNTT" NĂM 2019Với mục tiêu nâng cao năng lực quản lý và ứng dụng CNTT trong doanh nghiệp, chương trình "Hạt giống lãnh đạo CNTT" được cộng đồng CIO Vietnam tổ chức lần đầu tiên năm 2018 với 12 hạt giống được tuyển chọn và tham gia huấn luyện liên tục trong vòng 8 tháng ngoài những giá trị thiết thực cho thành viên tham dự mà còn một giá trị khác nữa đó chính là sự kết nối giữa các thành viên với Ban điều hành, Ban huấn luyện và các thành viên khác trong cộng đồngNếu bạn đang tìm kiếm một chương trình trải nghiệm thực tế từ chính những người đi trước là các Giám đốc CNTT trong DN để nâng cao năng lực ứng dụng CNTT và quản lý thì đây là chương trình phù hợp cho việc phát triển nghề nghiệp của bạn trong tương lai với 6 chủ đề khác nhauHãy đăng ký tham gia tại đây http://ciovn.org/cio-coaching-2019/', to_date('29-01-2019 13:15:00', 'dd-mm-yyyy hh24:mi:ss'), 26, 1, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (263, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/2149806681773123', '"Một trong những hoạt động của cộng đồng CIO Vietnam là chương trình tìm kiếm và huấn luyện trở thành """Hạt giống lãnh đạo CNTT''.Nếu bạn đang tìm kiếm cơ hội học hỏi, nâng cao năng lực bản thân trong lĩnh vực CNTT thì đừng bỏ qua cơ hội DUY NHẤT trong năm 2019 này.NHỮNG TIÊU CHÍ LỰA CHỌN """HẠT GIỐNG LÃNH ĐẠO CNTT""" NĂM 2019Ngày 17/1/2019 vừa qua, chương trình ''Hạt giống lãnh đạo CNTT""" đã chính thức bắt đầu tuyển 15 thành viên tham gia chương trình """Hạt giống lãnh đạo CNTT""" năm 2019 với 5 tiêu chí tham gia chương trìnhNăm 2018 với gần 100 thành viên đăng ký tham dự, cuối cùng chúng tôi đã tuyển chọn được 12 hạt giống tham gia chương trình huấn luyện này.Ban đang tìm kiếm một cơ hội phát triển nghề nghiệp trong năm 2019 thì đây là chương trình không thể bỏ qua do cộng đồng CIO Vietnam tổ chức với thông tin đăng ký tại https://goo.gl/axgWhBCơ hội chỉ có DUY NHẤT trong năm.Hãy đăng ký ngay hôm nay#CIOVietnam#CIOCoaching"', to_date('25-01-2019 18:08:00', 'dd-mm-yyyy hh24:mi:ss'), 33, 7, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (264, 641, 'https://www.facebook.com/groups/ciovietnam/permalink/2141615682592223', 'Cảm ơn mọi người đã dành thời gian tham dự sự kiện CIO Talks với chủ đề " Digital Transformation in Vietnam: Hype or Reality" do cộng đồng CIO Vietnam tổ chức.Để các khách tham dự có thể hiểu thêm về chủ đề này, CIO Vietnam xin gửi đến các anh/ chị slide tài liệu của 2 diễn giả trong chương trình lần này.Link download tài liệu của sự kiện: http://bit.ly/2sCVgVWCIO VIETNAM TEAM WITH LOVE <3', to_date('19-01-2019 22:30:00', 'dd-mm-yyyy hh24:mi:ss'), 23, 0, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (265, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/2141182035968921', '"Cộng đồng CIO Vietnam xin chia sẻ những hình ảnh của chương trình CIO Talks với chủ đề """Digital Transformation in Vietnam- Hype or Reality""" ngày 17/01/2019Ngày 17/01/2019 vừa qua, cộng đồng CIO Vietnam đã tổ chức thành công sự kiện CIO Talks với chủ đề “Digital Transformation in Vietnam: Hype or Reality”. CIO Vietnam Team xin cảm ơn hơn 80 anh/chị đã quan tâm và tham dự chương trình, tìm hiểu về vấn đề Chuyển đổi số trong nhân sự & cập nhật xu hướng công nghệ năm 2019.Đồng thời, cộng đồng CIO Vietnam cảm ơn sự tham gia của các anh/ chị là thành viên trong Ban cố vấn cộng đồng, các bạn tình nguyện viên đã hỗ trợ để buổi event diễn ra thành công.Cũng tại chương trình này, CIO Vietnam cũng khởi động chương trình “Hạt giống lãnh đạo CNTT 2019” được tổ chức lần 2 với với sự chia sẻ của anh Đoàn Đức Đề - Trưởng ban huấn luyện chương trình. Và thời gian đăng kí sẽ bắt đầu từ ngày 21/1/2019.Và năm nay cũng là năm đầu tiên CIO Vietnam ra mắt chương trình CIO Membership để tăng sự kết nối, mang lại nhiều giá trị, đồng thời chăm sóc các thành viên tại cộng đồng CIO Vietnam ngày một tốt hơn. Chiếc thẻ này sẽ đồng hành cùng các thành viên trong suốt thời gian hoạt động tại CIO Vietnam.Hẹn gặp các các anh chị tại sự kiện sắp tới của CIO Vietnam <3#CIOVietnam#CIOTalks#Digitaltransformation#CIOCoaching#CIOmembership"', to_date('19-01-2019 15:34:00', 'dd-mm-yyyy hh24:mi:ss'), 24, 2, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (266, 641, 'https://www.facebook.com/groups/ciovietnam/permalink/2139859212767870', '"Một trong những hoạt động đáng chú ý nhất của cộng đồng ITLC trong năm nay chính là sự kiện YEP. Sự kiện này là nơi quy tụ của các anh/ chị đang công tác trong lĩnh vực IT như: IT-Leader, IT Manager, CIO, CEO,…
Bên cạnh việc khẳng định vị thế của ITLC thông qua các hoạt động trong năm 2018 thì đây cũng là điểm đến, chia sẻ định hướng hoạt động cộng đồng, ngành nghề IT trong năm 2019.
Mời các anh/ chị quan tâm đăng kí tham dự chương trình:
Bước 1. Nhấn going tại sự kiện: https://www.facebook.com/events/126819934931318/
