insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (94, 641, 'https://www.facebook.com/groups/ciovietnam/permalink/2362570640496725', 'DESIGN THINKING - ỨNG DỤNG TRONG VẤN ĐỀ QUẢN LÝ, NHÂN SỰDesign thinking là cách tiếp cận dựa trên các giải pháp, thường quy định các giai đoạn & phương pháp cụ thể để giúp nhóm chiến lược và kinh doanh có thể đạt được những giải pháp nhằm cải thiện vấn đề và tập trung vào các đối tượng bị ảnh hưởng.Và sau đây là các giai đoạn tiêu biểu của Design Thinking trong việc ứng dụng & tận dụng ý tưởng đánh giá, phát triển quản lý nhân sự.1. Đồng cảm:Đối với các dự án Nhân sự, sự "đồng cảm" giúp nhà quản lý dự án và lãnh đạo nhân sự dành thời gian quan sát những nhân viên đang sử dụng công nghệ nhân sự trong công việc của họ. Tất cả các quyết định trong mảng Nhân sự thường xoay quanh việc lựa chọn phần mềm, hình thức triển khai và cấu hình và được thực hiện bởi nhóm trung tâm dự án, các cố vấn và nhân viên. Các quyết định này có thành công hay không là đến từ nhân viên và quá trình làm việc sau đó – đây là các đối tượng bị ảnh hưởng nhiều nhất bởi các công nghệ nhân sự mới.2. Xác địnhYếu tố quan trọng của Design Thinking chính là mục tiêu lấy “con người” làm điểm mấu chốt của vấn đề đang cần giải quyết. Và dùng yếu tố này để xác định cách giải quyết vấn đề trong các giai đoạn tiếp theo.3. Tìm ra các ý tưởngVới việc thấu hiểu mong muốn của nhân viên từ giai đoạn 1, cùng với những đặc điểm chi tiết được quan sát và mục tiêu giải quyết vấn đề rõ ràng theo cách tập trung vào con người, nhóm có thể bắt đầu các ý tưởng. Bằng việc suy nghĩ sáng tạo làm thế nào để đào tạo, phát triển và đạt được mục tiêu năng suất của tổ chức, nhà lãnh đạo nhân sự có thể thúc đẩy những thay đổi đáng kể về cách quản lý năng suất nhân viên và cách công nghệ nhân sự hỗ trợ đã và đang phát triển.4. Thử nghiệmTrong giai đoạn này, mục tiêu là tìm ra các giải pháp nhanh chóng, tinh gọn và khả thi, đồng thời đánh giá mức độ hiệu quả trong giải quyết vấn đề.5. Kiểm traDesign Thinking thách thức nhóm dự án đảm ', to_date('15-06-2019 23:15:00', 'dd-mm-yyyy hh24:mi:ss'), 20, 0, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (95, 641, 'https://www.facebook.com/groups/ciovietnam/permalink/2360720084015114', 'Cộng đồng CIO Vietnam thân mời anh/ chị tham dự chương trình CIO Talks “Design Thinking - Tư duy giải pháp đột phá” vào lúc 8h - 11h ngày 6/7/2019 tại Én Tea House & RestaurantLink đăng kí: http://ciovn.org/2019/06/06/design-thinking/', to_date('14-06-2019 21:36:00', 'dd-mm-yyyy hh24:mi:ss'), 22, 0, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (91, 641, 'https://www.facebook.com/groups/ciovietnam/permalink/2365091276911328', 'DESIGN THINKING & ỨNG DỤNG TRONG VIỆC CHĂM SÓC KHÁCH HÀNGSự trung thành của khách hàng là giá trị mà bất cứ một công ty, một thương hiệu nào mong muốn, đặc biệt là khi mà môi trường kinh doanh ngày càng trở nên cạnh tranh và tất cả các thị trường đều biến động với tốc độ nhanh hơn rất nhiều so với trước kia. Việc chăm sóc khách hàng là một trong những vấn đề nan giải cần giải quyết của mỗi doanh nghiệp. Làm thế nào để có thể chăm sóc khách hàng một cách hiệu quả nhất và ứng dụng phương pháp nào để thành viên trong một tổ chức thực hiện việc này một cách tốt nhất có thể ?Cộng đồng CIO Vietnam thân mời các anh/ chị tham gia chương trình "Design Thinking - Tư duy giải pháp đột phá" để cùng tìm ra câu trả lời cho những câu hỏi trên. Đặc biệt nội dung chia sẻ chính trong chương trình từ những khách mời có nhiều kinh nghiệm trong việc ứng dụng Design Thinking ở nhiều lĩnh vực khác nhau như: Government, Retail, Thương mại điện tử, Bank, phát triển các sản phẩm liên quan đến Digital Transformation.Thông tin chi tiết và link đăng ký về chương trình: http://bit.ly/2XdrQP5', to_date('17-06-2019 10:17:00', 'dd-mm-yyyy hh24:mi:ss'), 17, 0, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
