insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (162, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/2246236465463477', '"[THÔNG BÁO KẾT QUẢ CHƯƠNG TRÌNH HẠT GIỐNG LÃNH ĐẠO CNTT NĂM 2019]Với sứ mệnh kết nối, xây dựng một cộng đồng CNTT vững mạnh để nâng cao năng lực cá nhân và vai trò CNTT trong Doanh nghiệp, bên cạnh các chương trình #CIOTrends, #CIOTalks, #CIODebates cộng đồng CIO Vietnam thực hiện để chia sẻ về ứng dụng công nghệ, quản lý và lãnh đạo thì chương trình """Hạt giống lãnh đạo CNTT""" chỉ được tổ chức một lần DUY NHẤT trong một năm với phương pháp huấn luyện trực tiếp từ 5 Huấn luyện viên nhiều năm kinh nghiệm trong lĩnh vực CNTTChương trình """Hạt giống lãnh đạo CNTT""" năm 2019 trong 2 tháng tuyển sinh với 68 thành viên đăng ký tham gia chương trình năm nay, 34 thành viên vượt qua vòng sơ tuyển lần 1, tham gia phỏng vấn và 15 thành viên đã xuất sắc vượt qua vòng phỏng vấn để tham gia năm nay.Để tham gia chương trình này, mỗi thành viên cần hội tụ đủ 5 tiêu chí Hoài bão: Có ước mơ tạo ra các giải pháp CNTT đột phá mang lại giá trị cho doanh nghiệp Đam mê: Yêu thích nghề quản lý, lãnh đạo. Có kế hoạch cá nhân rõ ràng để thực hiện Nhiệt huyết: Không ngại khó, không ngại khổ, quyết đạt được mục tiêu đề ra Cam kết: Luôn giữ đúng cam kết và tuân thủ triệt để các quy tắc trong quá trình huấn luyệnTố chất: Có tố chất làm quản lý, lãnh đạoCộng đồng CIO Vietnam và Ban huấn luyện xin gửi lời chúc mừng tới 15 hạt giống lãnh đạo CNTT 2019 gồm có1. Anh Trịnh Đình Thắng- Technical Project Manager @ Ekino Vietnam2. Anh Mai Thanh Trung - Customer Success Manager @ Magestone3. Anh Phan Anh Phin- IT Manager TTC EDU4. Anh Trần Hải- Senjoir Database Administrator @ Robert Bosch Vietnam5. Anh Nguyễn Duy Thanh- Business Development Manager @ Splus Software JSC6. Anh Âu Gia Hiền- IT Manager @ VTV-Hyundai Home Shopping Co. Ltd.7. Anh Lê Hoài Văn- Front Office Applications Manager@ Central Group Vietnam8. Anh Văn Cộng Hậu- IT Service Manager @ Cty TNHH Dịch vụ Tin học 3H9. Chị Đoàn "', to_date('03-04-2019 08:02:00', 'dd-mm-yyyy hh24:mi:ss'), 39, 3, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (163, 641, 'https://www.facebook.com/groups/ciovietnam/permalink/2240399026047221', '"Thân gửi các anh/ chị một số hình ảnh trong sự kiện CIO Trends “Xu hướng và ứng dụng chuyển đổi số trong doanh nghiệp”.Một lần nữa, cộng đồng CIO Vietnam xin cảm ơn sự quan tâm, tham dự từ hơn 200 các CIO, CTO, CDO, CMO, CEO, IT Manager, Head of IT... và các quản lý cấp trung đến từ các công ty bán lẻ, phân phối, sản xuất logistic... tham dự không chỉ cập nhật xu hướng mà còn Networking tại khách sạn Tân Sơn Nhất, 200 Hoàng Văn Thụ, Phú Nhuận, HCMĐồng hành cùng với CIO Trends lần này có 11 nhà tài trợ, đồng hành như: #Freshworks, #CyberTraps, #VinaCIS, #Sophos, #SaoBacDau, #Mitek, #PATConsulting, #Abivin, #Baotin, #eDoctor,#Lifeplus, #NetNam... hình thức Business Matching giao lưu trực tiếp với thành viên tham dự lần này.
Ngoài ra còn có các cộng đồng, đối tác tham dự để kết nối, phát triển hợp tác trong thời gian tới như #LBC, #ITLC, #WISE, #SSES, #UAN, #TRGCũng tại CIO Trends lần này, cộng đồng CIO Vietnam tổng kết hoạt động năm 2018 với hai hoạt động chính
1. Báo cáo kết quả chương trình hợp tác huấn luyện """Hạt giống lãnh đạo CNTT""" năm 2018 có 12 hạt giống đã hoàn thành chương trình huấn luyện và thông báo 15 thành viên tham gia chương trình năm 20192. Vinh danh những cá nhân vì sự phát triển cộng đồng bao gồm: Ban cố vấn, Ban điều hành, Ban huấn luyện, những speaker tham gia chia sẻ trong các chương trình #CIOTRENDS, #CIOTALKS, #CIODEBATES, #WORKSHOPS năm 2018 cùng toàn bộ team supporter, coordinater từ trước nayCIOVietnam trân trọng cảm ơn sự có mặt, đóng góp, hỗ trợ của tất cả các anh/chị/bạn trong chương trình CIO Trends lần này và trong suốt từ năm 2010 đến nayHẹn sớm gặp các anh/chị và các bạn!Thông tin liên hệ: ciovietnamteam@gmail.comCIO Vietnam Team with LOVE ❤Ngày 28/3/2019 vừa qua, với chủ đề """Xu hướng và ứng dụng chuyển đổi số trong DN""" đã nhận được sự quan tâm, tham dự từ hơn 200 các CIO, CTO, CDO, CMO, CEO, IT Manager, Head of IT... và các quản lý "', to_date('30-03-2019 08:31:00', 'dd-mm-yyyy hh24:mi:ss'), 43, 10, 0);

