insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (216, 446, 'https://www.facebook.com/groups/ciovietnam/permalink/1263715980382202', '"Cộng đồng CIO Viet Nam, có mong muốn làm một bảng phân loại và giới thiệu về các nhà cung cấp dịch vụ điện toán đám mây trong nước (IaaS, PaaS và SaaS giống như trong hình). Các anh chị nào là những công ty cung cấp dịch vụ này xin vui lòng inbox cho chúng tôi nhé.
