insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (174, 544, 'https://www.facebook.com/groups/ciovietnam/permalink/2233494860070971', 'Mối liên hệ chặt chẽ giữa Thương mại và công nghệ ngày càng được thể hiện rõ trong việc định hình cách thức mà chúng ta giao dịch. Những sự tiến bộ của công nghệ Trí tuệ nhân tạo, Blockchain, in 3D... là chìa khóa mở ra nhiều cơ hội phát triển cho các cá nhân, doanh nghiệp. Tiến bộ công nghệ không phải là một sự đảm bảo tăng trưởng thương mại, tuy vậy sự thay đổi thành công về mặt cấu trúc vận hành được thúc đẩy bởi công nghệ là yếu tố trung tâm để đảm bảo rằng tất cả các bên đều có lợi. Vì thế, chúng ta cần tìm hiểu cách khai thác những điều mới mẻ này để định lượng được mức độ thương mại toàn cầu có thể bị ảnh hưởng bởi xu hướng chuyển đổi số.Vân đính kèm Report của Tổ chức Thương mại Thế giới (2018) để anh chị tìm hiểu kỹ hơn về sự tác động của Chuyển đổi số đến Thương mại toàn cầu.Nice week anh chị.', to_date('25-03-2019 14:49:00', 'dd-mm-yyyy hh24:mi:ss'), 3, 0, 0);

