insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (221, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/2204306232989834', 'Hạ tầng cơ sở hệ thống thông tin là nền tảng vững chắc cho mọi hoạt động và ứng dụng liên quan đến công nghệ thông tin của một doanh nghiệp. Một hệ thống thông tin ổn định hoặc bị ngắt quãng có thể gây ảnh hưởng nghiệm trọng tới hiệu quả và hiệu suất kinh doanh của doanh nghiệpGần đây, một trong những xu hướng phổ biến trong Công nghệ thông tin (CNTT) là chuyển đổi số và tất nhiên đi kèm với nó là chuyển đổi cơ sở hạ tầng CNTT. Nhưng để trở thành một doanh nghiệp kỹ thuật số không đơn giản chỉ là xây dựng các ứng dụng di động.Vậy làm thế nào để "Chuyển đổi cơ sở hạ tầng CNTT- Xương sống của cuộc cách mạng kỹ thuật số" hiện nay?#CIOVietnam xin chia sẻ một bài viết về cơ sở hạ tầng CNTT từ một thành viên tham gia chương trình "Hạt giống lãnh đạo CNTT" do cộng đồng #ITLC tổ chức nay cùng tham gia góp phần vào phát triển cộng đồng bằng cách là một trong những huấn luyện viên tập sự trong năm nay-> Mời bạn đọc toàn bộ bài viết ở đây https://goo.gl/oBGEZL-> Nếu bạn muốn tham gia chương trình "Hạt giống lãnh đạo CNTT" năm 2019 thì đăng ký tại đây https://goo.gl/aqJWY1-> Đăng ký tham gia chương trình CIO Trends để cập nhật xu hướng và ứng dụng CNTT tại đây https://goo.gl/XdxCMxToàn bộ chương trình về CIO Trends bạn có thể xem tại đây https://goo.gl/RNvDNr#CIOVietnam', to_date('03-03-2019 15:54:00', 'dd-mm-yyyy hh24:mi:ss'), 17, 1, 0);

