insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (127, 641, 'https://www.facebook.com/groups/ciovietnam/permalink/2301915899895533', '"Những năm gần đây, cụm từ """CMCN 4.0""" đã bắt đầu phổ biến tại Việt Nam. Tuy nhiên, năng lực lãnh đạo nào cho thời đại mới, thời đại bùng nổ hội nhập & toàn cầu hoá ? Các nhà lãnh đạo Việt Nam cần xác định chiến lược phát triển ra sao để luôn dẫn dắt tổ chức & giữ vững vị thế của mình ?Để trả lời những câu hỏi trên, trước hết, các nhà lãnh đạo cần chuẩn bị cho mình một tư duy lãnh đạo phù hợp để đương đầu với tốc độ, sự biến động của thị trường hiện nay. Hay nói đúng hơn, đã đến lúc để """định nghĩa lại lãnh đạo""".Với mục đích quy tụ những chuyên gia giỏi nhất để định hình lại vai trò của CIO trong Doanh nghiệp, cộng đồng CIO Vietnam trân trọng giới thiệu chương trình & các diễn giả tham dự trong sự kiện reThink CIO Forum by CIO Academy Asia """CHARTING A NEW COURSE: Rethinking Your Strategies For The Digital Economy""" được tổ chức vào ngày 15/5/2019 tại Sheraton Hotel & Towers, 88 Đồng Khởi, Bến Nghé, Quận 1, HCM.Kính mời các anh/ chị quan tâm đến chủ đề đăng kí tham dự với thông tin chi tiết tại đây http://ciovn.org/rethink-cio-forum-vietnam-2019/Mọi thắc mắc về chương trình vui lòng liên hệ:Ms. Thảo Trang ☎️ 077 3700 080Ms. Bảo Hân ☎️ 0911 506 399"', to_date('10-05-2019 18:23:00', 'dd-mm-yyyy hh24:mi:ss'), 21, 1, 0);

