insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (236, 641, 'https://www.facebook.com/groups/ciovietnam/permalink/2190026567751134', '"CIO VIETNAM MEMBERSHIP - KẾT NỐI THÀNH VIÊN CỘNG ĐỒNG CIO VIETNAMNhững ưu đãi của chương trình Membership CIO Vietnam vẫn liên tục được cập nhật đến các member. Với mục tiêu Kết nối- Chia sẻ kinh nghiệm- Học hỏi- Nâng cao năng lực cá nhân cùng với nhiều lợi ích khác sẽ được cập nhật trong quá trình hoạt động với thông tin đăng ký tại đây: http://ciovn.org/chuong-trinh-cio-vietnam-membership/Mọi thắc mắc về chương trình vui lòng liên hệ:
Thuỷ Đặng - redcloudvn@gmail - 0938 298 278
Vy Nguyễn - vy.nguyen@ciovn.org - 0397 906 799"', to_date('21-02-2019 20:45:00', 'dd-mm-yyyy hh24:mi:ss'), 15, 11, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (258, 602, 'https://www.facebook.com/groups/ciovietnam/permalink/2174358502651274', ' KẾT NỐI- HỌC HỎI KINH NGHIỆM ĐỂ PHÁT TRIỂN BẢN THÂNĐó là mục tiêu từ mỗi thành viên tham gia cộng đồng CIO Vietnam trong suốt thời gian qua.Để thực hiện điều đó, cộng đồng CIO Vietnam từ năm 2010 đến nay đã tổ chức được hơn 70 chủ đề khác nhau chia sẻ về kiến thức, kinh nghiệm trong lĩnh vực CNTT thông qua các chương trình CIO Trends, CIO Talks, CIO Debates và chương trình “Hạt giống lãnh đạo CNTT” bắt đầu từ năm 2018 mang lại những giá trị nhất định cho từng thành viên.Làm thế nào cộng đồng CIO Vietnam mang lại nhiều giá trị hơn nữa? gắn kết những thành viên hơn nữa? nâng cao vai trò IT trong doanh nghiệp để rút ngắn khoảng cách giữa IT và Business?Những điều trên là nỗi trăn trở của Ban cố vấn, Ban điều hành Ban huấn luyện, Admin, Mode và các thành viên tham gia phát triển cộng đồng CIO Vietnam Năm 2019, cộng đồng CIO Vietnam sẽ có những hoạt động chia sẻ tập trung hơn thông qua các chương trình CO Trends, CIO Talks, CIO Debates và những chương trình đặc biệt khác, hứa hẹn một năm với nhiều trải nghiệm và giá trị thực thế hơn Chương trình “ Hạt giống lãnh đạo CNTT” đang được thực hiện với thông tin đăng ký tại đây http://ciovn.org/cio-coaching-2019/ Chúng tôi quan niệm mỗi cá nhân đều có gì đó để chia sẻ để học hỏi lẫn nhau vì vậy chương trình “CIO Membership” ra đời với mục tiêu Kết nối- Chia sẻ kinh nghiệm- Học hỏi- Nâng cao năng lực cá nhân cùng với nhiều lợi ích khác sẽ được cập nhật trong quá trình hoạt động với thông tin đăng ký tại đây http://ciovn.org/chuong-trinh-cio-vietnam-membership/. Chương trình này dành cho những thành viên có 4 mục tiêu trên và chúng ta sẽ đồng hành cùng nhau thông qua những hoạt động dành riêng cho CIO Member Và chương trình CIO Trends, sẽ được tổ chức vào ngày 28/3/2018 với mục tiêu cập nhật xu hướng công nghệ và tổng quan về tình hình ứng dụng CNTT trong việc điều hành và quản lý trong doanh nghiệp.Đầu năm, xin chúc cả ', to_date('11-02-2019 14:15:00', 'dd-mm-yyyy hh24:mi:ss'), 23, 1, 0);

